INSERT INTO `posts` (`id`, `title`, `description`, `content`, `author`, `category`, `image`, `created_at`) VALUES
(10, 'Vô địch V-League lần đầu tiên, Quảng Nam nhận thư chúc mừng từ FIFA', 'Chủ tịch FIFA Gianni Infantino gửi thư chúc mừng CLB Quảng Nam giành danh hiệu vô địch V-League 2017.', '<p style="box-sizing: border-box; margin: 0px 0px 8px; font-family: Arial, sans-serif; color: #111111; line-height: 22px; font-size: 16px; text-align: justify;">Chức v&ocirc; địch V-League 2017 l&agrave; lần đầu ti&ecirc;n trong lịch sử&nbsp;<span style="box-sizing: border-box; font-weight: bold; margin-top: 0px; margin-bottom: 8px; line-height: 22px;">CLB Quảng Nam</span>&nbsp;l&ecirc;n ng&ocirc;i tại giải đấu hạng cao nhất của b&oacute;ng đ&aacute; chuy&ecirc;n nghiệp Việt Nam. Đội b&oacute;ng dưới sự dẫn dắt của HLV Ho&agrave;ng Văn Ph&uacute;c gi&agrave;nh 48 điểm sau 26 v&ograve;ng đấu, vượt qua đội &aacute; qu&acirc;n FLC Thanh H&oacute;a nhờ hơn th&agrave;nh t&iacute;ch đối đầu.</p>\r\n<p style="box-sizing: border-box; margin: 0px 0px 8px; font-family: Arial, sans-serif; color: #111111; line-height: 22px; font-size: 16px; text-align: justify;">Sau khi đạt th&agrave;nh t&iacute;ch đầy bất ngờ n&agrave;y, CLB Quảng Nam nhận được thư ch&uacute;c mừng từ Chủ tịch LĐBĐ thế giới Gianni Infantino.</p>\r\n<p style="box-sizing: border-box; margin: 0px 0px 8px; font-family: Arial, sans-serif; color: #111111; line-height: 22px; font-size: 16px; text-align: justify;">"Th&agrave;nh t&iacute;ch c&oacute; được của CLB Quảng Nam l&agrave; kết quả của sự quyết t&acirc;m của tất cả c&aacute;c th&agrave;nh vi&ecirc;n CLB. T&ocirc;i gửi lời ch&uacute;c tới tất cả c&aacute;c cầu thủ, huấn luyện vi&ecirc;n trưởng, đội ngũ trợ l&yacute; v&agrave; b&aacute;c sỹ cũng như to&agrave;n thể người h&acirc;m mộ tỉnh Quảng Nam với th&agrave;nh t&iacute;ch ấn tượng n&agrave;y&rdquo;, người đứng đầu FIFA chia sẻ, đồng thời gửi lời cảm ơn tới CLB Quảng Nam v&agrave; VFF v&igrave; đ&atilde; lan tỏa những th&ocirc;ng điệp t&iacute;ch cực của b&oacute;ng đ&aacute;.</p>\r\n<figure class="expNoEdit" style="box-sizing: border-box; font-family: Arial, sans-serif; color: #111111; text-align: center; width: 685px; margin: 0px auto 8px !important;"><img style="box-sizing: border-box; object-fit: cover; border: 0px; vertical-align: middle; max-width: 100%; cursor: pointer; height: auto !important; margin: 0px !important;" src="https://image.vtcns.com/resize/685x406/files/ngoc.anh/2017/12/01/quang-nam-1017219.jpg" alt="Vo dich V-League lan dau tien, Quang Nam nhan thu chuc mung tu FIFA hinh anh 1" width="685" height="406" />\r\n<figcaption style="box-sizing: border-box; background: #f2f2f2; padding: 3px; margin: 0px auto; width: 691px; font-size: 15px !important;">\r\n<p class="expEdit" style="box-sizing: border-box; margin: 0px; font-family: Arial; line-height: 19px; font-style: italic;">&nbsp;Quảng Nam v&ocirc; địch V-League 2017.</p>\r\n</figcaption>\r\n</figure>\r\n<div class="adv" style="box-sizing: border-box; font-family: Arial, sans-serif; color: #111111; max-width: 100%; text-align: center; margin: 0px; overflow: hidden;">&nbsp;</div>\r\n<p style="box-sizing: border-box; margin: 0px 0px 8px; font-family: Arial, sans-serif; color: #111111; line-height: 22px; font-size: 16px; text-align: justify;">Với tư c&aacute;ch l&agrave; đội v&ocirc; địch V-League, Quảng Nam sẽ l&agrave; đại diện của Việt Nam g&oacute;p mặt ở v&ograve;ng sơ loại thứ hai của AFC Champions League. Nếu kh&ocirc;ng vượt qua được v&ograve;ng sơ loại v&agrave; v&ograve;ng play-off của giải đấu n&agrave;y, thầy tr&ograve; HLV Ho&agrave;ng Văn Ph&uacute;c sẽ tham dự v&ograve;ng bảng AFC Cup c&ugrave;ng S&ocirc;ng Lam Nghệ An, đội t&acirc;n v&ocirc; địch C&uacute;p quốc gia.</p>\r\n<p style="box-sizing: border-box; margin: 0px 0px 8px; font-family: Arial, sans-serif; color: #111111; line-height: 22px; font-size: 16px; text-align: justify;">D&ugrave; vậy, đang c&oacute; nhiều tin đồn về việc CLB Quảng Nam c&oacute; nguy cơ kh&ocirc;ng được tham dự đấu trường ch&acirc;u &Aacute; trong m&ugrave;a giải 2018 do kh&ocirc;ng đ&aacute;p ứng đủ điều kiện của AFC. Theo đ&oacute;, đội b&oacute;ng n&agrave;y chưa c&oacute; đội U17 v&agrave; cũng chưa c&oacute; cơ sở vật chất đủ ti&ecirc;u chuẩn để tổ chức thi đấu c&aacute;c trận đấu quốc tế.</p>\r\n<p style="box-sizing: border-box; margin: 0px 0px 8px; font-family: Arial, sans-serif; color: #111111; line-height: 22px; font-size: 16px; text-align: justify;">Trong trường hợp Quảng Nam kh&ocirc;ng được tham dự AFC Champions League v&agrave; AFC Cup (chưa c&oacute; kết luận ch&iacute;nh thức), FLC Thanh H&oacute;a sẽ l&agrave; đội thay thế.</p>\r\n<p>&nbsp;</p>', 'Admin', 'Thể thao', 'uploads/quang-nam-1017219.jpg', '2017-12-01 12:55:35'),
(11, 'Đề xuất cải tiến chữ viết tiếng Việt gây tranh cãi: Bộ GD-ĐT thông tin chính thức', ' Bộ Giáo dục và Đào tạo cho biết sẽ không dự kiến áp dụng bất cứ phương án nào về cải tiến chữ viết của PGS Bùi Hiền', '<p style="box-sizing: border-box; margin: 0px 0px 8px; font-family: Arial, sans-serif; color: #111111; line-height: 22px; font-size: 16px; text-align: justify;">Những ng&agrave;y qua,<span style="box-sizing: border-box; font-weight: bold; margin-top: 0px; margin-bottom: 8px; line-height: 22px;">&nbsp;đề xuất cải tiến chữ viết tiếng Việt</span>&nbsp; "gi&aacute;o dục" th&agrave;nh "z&aacute;o zụk", "Tiếng Việt" th&agrave;nh "Tiếq Việt" của PGS.TS B&ugrave;i Hiền (Nguy&ecirc;n Ph&oacute; hiệu trưởng Đại họcSư phạm Ngoại ngữ H&agrave; Nội, nguy&ecirc;n Ph&oacute; viện trưởng Viện Nội dung &amp; Phương ph&aacute;p dạy - học phổ th&ocirc;ng) nhận được nhiều &yacute; kiến tranh luận.</p>\r\n<p style="box-sizing: border-box; margin: 0px 0px 8px; font-family: Arial, sans-serif; color: #111111; line-height: 22px; font-size: 16px; text-align: justify;">Mới đ&acirc;y,<em style="box-sizing: border-box; margin-top: 0px; margin-bottom: 8px; line-height: 22px;">&nbsp;Bộ GD-ĐT c&oacute; th&ocirc;ng c&aacute;o b&aacute;o ch&iacute; li&ecirc;n quan việc đề xuất cải tiến chữ viết của PGS.TS B&ugrave;i Hiền</em>.</p>\r\n<figure class="expNoEdit" style="box-sizing: border-box; font-family: Arial, sans-serif; color: #111111; text-align: center; width: 685px; margin: 0px auto 8px !important;"><img style="box-sizing: border-box; object-fit: cover; border: 0px; vertical-align: middle; max-width: 100%; cursor: pointer; height: auto !important; margin: 0px !important;" src="https://image.vtcns.com/resize/685x369/files/phamthinh/2017/11/30/bui-hien-6-3-1201224.jpg" alt="De xuat cai tien chu viet tieng Viet gay tranh cai: Bo GD-DT thong tin chinh thuc hinh anh 1" width="685" height="369" />\r\n<figcaption style="box-sizing: border-box; background: #f2f2f2; padding: 3px; margin: 0px auto; width: 691px; font-size: 15px !important;">\r\n<p class="expEdit" style="box-sizing: border-box; margin: 0px; font-family: Arial; line-height: 19px; font-style: italic;">&nbsp;PGS. TS B&ugrave;i Hiền - t&aacute;c giả c&ocirc;ng tr&igrave;nh nghi&ecirc;n cứu cải tiến chữ viết tiếng Việt g&acirc;y tranh c&atilde;i.&nbsp;</p>\r\n</figcaption>\r\n</figure>\r\n<p style="box-sizing: border-box; margin: 0px 0px 8px; font-family: Arial, sans-serif; color: #111111; line-height: 22px; font-size: 16px; text-align: justify;">Theo Bộ GD-ĐT, &yacute; kiến của&nbsp;<span style="box-sizing: border-box; text-decoration-line: underline;">PGS.TS B&ugrave;i Hiền</span>&nbsp;về cải tiến chữ quốc ngữ l&agrave; đề xuất trong một hội thảo khoa học của ng&agrave;nh Ng&ocirc;n ngữ.</p>\r\n<p style="box-sizing: border-box; margin: 0px 0px 8px; font-family: Arial, sans-serif; color: #111111; line-height: 22px; font-size: 16px; text-align: justify;">Cụ thể, đ&oacute; l&agrave; hội thảo &ldquo;Ng&ocirc;n ngữ ở Việt Nam: Hội nhập v&agrave; Ph&aacute;t triển&rdquo;, do Hội Ng&ocirc;n ngữ học Việt Nam v&agrave; ĐH Quy Nhơn tổ chức từ th&aacute;ng 7/2017.</p>\r\n<p style="box-sizing: border-box; margin: 0px 0px 8px; font-family: Arial, sans-serif; color: #111111; line-height: 22px; font-size: 16px; text-align: justify;">Bộ GD-ĐT c&oacute; quan điểm tr&acirc;n trọng tất cả c&ocirc;ng tr&igrave;nh nghi&ecirc;n cứu v&agrave; đề xuất nghi&ecirc;m t&uacute;c của c&aacute;c nh&agrave; khoa học.</p>\r\n<div class="adv" style="box-sizing: border-box; font-family: Arial, sans-serif; color: #111111; max-width: 100%; text-align: center; margin: 0px; overflow: hidden;">&nbsp;</div>\r\n<figure class="expNoEdit" style="box-sizing: border-box; font-family: Arial, sans-serif; color: #111111; text-align: center; width: 685px; margin: 0px auto 8px !important;"><img style="box-sizing: border-box; object-fit: cover; border: 0px; vertical-align: middle; max-width: 100%; cursor: pointer; height: auto !important; margin: 0px !important;" src="https://image.vtcns.com/files/ctv.giaoduc/2017/11/30/75a17a8248c6efimg-1356259-1055359.jpg" alt="De xuat cai tien chu viet tieng Viet gay tranh cai: Bo GD-DT thong tin chinh thuc hinh anh 2" width="600" height="380" />\r\n<figcaption style="box-sizing: border-box; background: #f2f2f2; padding: 3px; margin: 0px auto; width: 606px; font-size: 15px !important;">\r\n<p class="expEdit" style="box-sizing: border-box; margin: 0px; font-family: Arial; line-height: 19px; font-style: italic;"><em style="box-sizing: border-box; font-family: Arial, sans-serif; margin-top: 0px; margin-bottom: 8px; line-height: 22px; font-size: 16px;">&nbsp;Đoạn văn bản sau khi tiếng Việt được cải c&aacute;ch theo nghi&ecirc;n cứu của PGS. TS B&ugrave;i Hiền.</em></p>\r\n</figcaption>\r\n</figure>\r\n<p style="box-sizing: border-box; margin: 0px 0px 8px; font-family: Arial, sans-serif; color: #111111; line-height: 22px; font-size: 16px; text-align: justify;">Tuy nhi&ecirc;n, Bộ GD-ĐT cho rằng để đưa một đề xuất li&ecirc;n quan vấn đề cải tiến chữ viết của ng&ocirc;n ngữ quốc gia v&agrave;o thực tế cần c&oacute; sự thẩm định của c&aacute;c chuy&ecirc;n gia, &yacute; kiến của nhiều tầng lớp nh&acirc;n d&acirc;n v&agrave; sự xem x&eacute;t, quyết định của Quốc hội, Ch&iacute;nh phủ.</p>\r\n<p style="box-sizing: border-box; margin: 0px 0px 8px; font-family: Arial, sans-serif; color: #111111; line-height: 22px; font-size: 16px; text-align: justify;">Bộ GD-ĐT kh&ocirc;ng đủ thẩm quyền v&agrave; kh&ocirc;ng dự kiến &aacute;p dụng bất cứ phương &aacute;n n&agrave;o về cải tiến chữ viết quốc gia trong giai đoạn hiện nay.</p>', 'Admin', 'Giáo dục', 'uploads/bui-hien-6-3-1201224.jpg', '2017-12-01 12:58:30'),
(12, 'Những chi tiết thú vị trong "Avengers: Infinity War"', 'Chỉ với đoạn trailer dài hơn 2 phút, rất nhiều tình tiết mới của bộ phim đã được hé lộ khiến các fan vô cùng hào hứng.', '<p style="font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 1.4; text-align: justify;">Sau thời gian d&agrave;i khiến c&aacute;c fan mong mỏi, h&atilde;ng Marvel vừa tung ra trailer đầy hấp dẫn với tập hợp to&agrave;n bộ d&agrave;n si&ecirc;u anh h&ugrave;ng trong trận chiến ho&agrave;nh tr&aacute;ng nhất lịch sử.&nbsp;<em>Avengers: Infinity War</em>&nbsp;ch&iacute;nh l&agrave; bộ phim quy tụ đ&ocirc;ng đảo si&ecirc;u anh h&ugrave;ng nhất từ trước tới giờ của Marvel Studios. Qua đoạn trailer d&agrave;i gần 2 ph&uacute;t rưỡi, kh&aacute;n giả đ&atilde; nhanh ch&oacute;ng nhận ra rất nhiều chi tiết th&uacute; vị v&agrave; đầy hấp dẫn.</p>\r\n<p style="font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 1.4; text-align: justify;"><strong>Ngoại h&igrave;nh mới</strong></p>\r\n<p style="font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 1.4; text-align: justify;">Đầu ti&ecirc;n, đ&oacute; l&agrave; c&aacute;c diễn vi&ecirc;n. Sự thay đổi diện mạo của họ đ&atilde; khiến c&aacute;c fan v&ocirc; c&ugrave;ng phấn kh&iacute;ch. Hơn hết sự thay đổi n&agrave;y&nbsp;đem tới một l&agrave;n gi&oacute; mới lạ cho bộ phim.&nbsp;</p>\r\n<p style="font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 1.4;" align="center"><img class="news-image" style="border: 0px; max-width: 660px;" src="http://image.24h.com.vn/upload/4-2017/images/2017-12-01/1512098470-804-nguoi-ham-mo-phat-cuong-vi-nhung-chi-tiet-thu-vi-trong-avengers-infinity-war-avengers-infinity-war-image-chris-evans-captain-am-1512095196-width600height246.jpg" alt="Những chi tiết th&uacute; vị trong &amp;#34;Avengers: Infinity War&amp;#34; - 1" /></p>\r\n<p style="font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 1.4; color: #0000ff; font-style: italic; text-align: center;">Captain America tr&ocirc;ng nam t&iacute;nh v&agrave; mạnh mẽ hơn trong diện mạo mới</p>\r\n<p style="font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 1.4; text-align: justify;">Bộ r&acirc;u quai n&oacute;n của Captain America l&agrave; điều đầu ti&ecirc;n khiến kh&aacute;n giả phải "giật m&igrave;nh" th&uacute; vị. C&oacute; lẽ tương tự như việc Thor cắt t&oacute;c, ngoại h&igrave;nh mới của Captain America tượng trưng cho quyết t&acirc;m v&agrave; sức mạnh mới trong cuộc chiến khốc liệt sắp tới.</p>\r\n<p style="font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 1.4;" align="center"><img class="news-image" style="border: 0px; max-width: 660px;" src="http://image.24h.com.vn/upload/4-2017/images/2017-12-01/1512098471-218-nguoi-ham-mo-phat-cuong-vi-nhung-chi-tiet-thu-vi-trong-avengers-infinity-war-avengers-infinity-war-image-scarlett-johansson-600-1512095278-width600height246.jpg" alt="Những chi tiết th&uacute; vị trong &amp;#34;Avengers: Infinity War&amp;#34; - 2" /></p>\r\n<p style="font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 1.4; color: #0000ff; font-style: italic; text-align: center;" align="center">M&aacute;i t&oacute;c v&agrave;ng đầy c&aacute; t&iacute;nh của Black Widow</p>\r\n<p style="font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 1.4; text-align: justify;">Black Widow tr&uacute;t bỏ m&aacute;i t&oacute;c đỏ quen thuộc để chuyển sang m&agrave;u t&oacute;c v&agrave;ng. B&ecirc;n cạnh sự thay đổi của Captain America v&agrave; Black Widow, Spider-Man cũng xuất hiện với bộ gi&aacute;p mới toanh, c&oacute; vẻ như l&agrave; sản phẩm được Iron Man n&acirc;ng cấp cho trận chiến mới.</p>\r\n<p style="font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 1.4; text-align: justify;"><strong>Những chi tiết t&igrave;nh cảm</strong></p>\r\n<p style="font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 1.4;" align="center"><img class="news-image" style="border: 0px; max-width: 660px;" src="http://image.24h.com.vn/upload/4-2017/images/2017-12-01/1512098471-323-nguoi-ham-mo-phat-cuong-vi-nhung-chi-tiet-thu-vi-trong-avengers-infinity-war-avengers-infinity-war-image-paul-bettany-600x247-1-1512095367-width600height247.jpg" alt="Những chi tiết th&uacute; vị trong &amp;#34;Avengers: Infinity War&amp;#34; - 3" /></p>\r\n<p style="font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 1.4; color: #0000ff; font-style: italic; text-align: center;">Wanda v&agrave; Vision trao nhau c&aacute;i nh&igrave;n &acirc;u yếm</p>\r\n<p style="font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 1.4; text-align: justify;">Giống trong nguy&ecirc;n t&aacute;c truyện tranh, ph&ugrave; thủy Wanda v&agrave; si&ecirc;u anh h&ugrave;ng Vision bắt đầu nảy sinh t&igrave;nh cảm với nhau, bằng chứng l&agrave; cảnh phim hai người trao nhau &aacute;nh nh&igrave;n &acirc;u yếm.</p>\r\n<p style="font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 1.4;" align="center">&nbsp;</p>\r\n<div style="font-family: Arial, Helvetica, sans-serif; font-size: 12px; text-align: center; width: 699px; clear: both; position: relative;">\r\n<div style="position: relative; max-width: 660px; margin: 0px auto;"><img class="news-image" style="border: 0px; max-width: 660px;" src="http://image.24h.com.vn/upload/4-2017/images/2017-12-01/1512098471-623-nguoi-ham-mo-phat-cuong-vi-nhung-chi-tiet-thu-vi-trong-avengers-infinity-war-a11-1512095434-width660height270.jpg" alt="Những chi tiết th&uacute; vị trong &amp;#34;Avengers: Infinity War&amp;#34; - 4" />\r\n<div id="24h-banner-in-image" style="position: absolute; overflow: hidden; width: 660px; height: 90px; color: white; bottom: 0px; left: 0px;">&nbsp;</div>\r\n</div>\r\n</div>\r\n<p style="font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 1.4; text-align: justify;">&nbsp;</p>\r\n<p style="font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 1.4; color: #0000ff; font-style: italic; text-align: center;" align="center">Tony Stark sẽ nắm tay ai?</p>\r\n<p style="font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 1.4; text-align: justify;">&nbsp;</p>\r\n<p style="font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 1.4; text-align: justify;">&nbsp;</p>\r\n<p style="font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 1.4; text-align: justify;">&nbsp;</p>', 'mins', 'Giải trí', 'uploads/ccc.jpg', '2017-12-01 14:11:39'),
(13, 'Ngoại hạng Anh trước vòng 15: Arsenal - MU dấn thân vào "tử địa"', 'Với việc 2 đối thủ đang có phong độ cực cao là Arsenal và MU phải gặp nhau, Chelsea, Man City và Liverpool đều sẽ thắng bằng mọi giá tại vòng 15.', '<p style="font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 1.4; text-align: justify;"><strong>Arsenal - MU nhuộm đỏ trời Anh</strong></p>\r\n<p style="font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 1.4; text-align: justify;">V&ograve;ng 15 diễn ra chỉ chưa đầy 3 ng&agrave;y sau khi kết th&uacute;c v&ograve;ng 14. Với quỹ thời gian eo hẹp đ&oacute;, kh&oacute; c&oacute; đội b&oacute;ng n&agrave;o giữ được thể lực tốt nhất cho c&aacute;c cầu thủ nếu kh&ocirc;ng biết kh&eacute;o l&eacute;o xoay tua đội h&igrave;nh.</p>\r\n<p style="font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 1.4;" align="center"><img class="news-image" style="border: 0px; max-width: 660px;" src="http://image.24h.com.vn/upload/4-2017/images/2017-11-30/Ngoai-hang-Anh-truoc-vong-15-MU---Arsenal-nhuom-do-troi-Anh-mu-1512039841-646-width660height371.jpg" alt="Ngoại hạng Anh trước v&ograve;ng 15: Arsenal - MU dấn th&acirc;n v&agrave;o &amp;#34;tử địa&amp;#34; - 1" /></p>\r\n<p style="font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 1.4; color: #0000ff; font-style: italic; text-align: center;">MU lẫn&nbsp;<a class="TextlinkBaiviet" style="text-decoration-line: none; color: #0000ff;" title="Arsenal" href="http://www.24h.com.vn/arsenal-fc-c48e3424.html">Arsenal</a>&nbsp;đều đang c&oacute; phong độ cao</p>\r\n<p style="font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 1.4; text-align: justify;">Arsenal v&agrave; MU l&agrave; 2 đội b&oacute;ng ghi được nhiều b&agrave;n thắng nhất tại v&ograve;ng 14. Nếu như Arsenal c&oacute; điểm tựa s&acirc;n nh&agrave; v&agrave; bộ 3 Lacazette - Ozil - Sanchez đang thăng hoa, MU lại c&oacute; sự xuất hiện của Paul Pogba, Zlatan Ibrahimovic c&ugrave;ng Ashley Young v&agrave; Martial đang c&oacute; phong độ cực cao.</p>\r\n<p style="font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 1.4; text-align: justify;">Marcus Rashford v&agrave; Martial đ&atilde; được HLV Jose Mourinho ph&acirc;n bố số ph&uacute;t thi đấu hợp l&yacute; tại giữa tuần n&ecirc;n cả 2 sẽ c&oacute; được thể lực tốt nhất. Thiếu vắng đ&aacute;ng tiếc nhất trong trận đại chiến n&agrave;y nhiều khả năng sẽ l&agrave; Nemanja Matic khi t&acirc;n binh của MU đ&atilde; phải tập tễnh rời s&acirc;n sớm trong trận đấu gặp Watford đ&ecirc;m thứ 3.</p>\r\n<p style="font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 1.4;" align="center"><img class="news-image" style="border: 0px; max-width: 660px;" src="http://image.24h.com.vn/upload/4-2017/images/2017-11-30/Ngoai-hang-Anh-truoc-vong-15-MU---Arsenal-nhuom-do-troi-Anh-young-1512039660-559-width660height440.jpg" alt="Ngoại hạng Anh trước v&ograve;ng 15: Arsenal - MU dấn th&acirc;n v&agrave;o &amp;#34;tử địa&amp;#34; - 2" /></p>\r\n<p style="font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 1.4; color: #0000ff; font-style: italic; text-align: center;">Ashley Young đang ở tr&ecirc;n đỉnh cao</p>\r\n<p style="font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 1.4; text-align: justify;">&nbsp;</p>\r\n<div class="banner_inread" style="font-family: Arial, Helvetica, sans-serif; font-size: 12px; text-align: justify;">&nbsp;</div>\r\n<p style="font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 1.4; text-align: justify;">&nbsp;</p>\r\n<p style="font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 1.4; text-align: justify;">Với đầy đủ binh h&ugrave;ng, tướng mạnh tr&ecirc;n h&agrave;ng c&ocirc;ng, đ&acirc;y sẽ l&agrave; trận đấu hấp dẫn. Ngo&agrave;i ra,&nbsp;Arsenal c&oacute; lối chơi kỹ thuật sẽ l&agrave; ph&eacute;p thử tuyệt vời để Mourinho chuẩn bị cho trận đại chiến gặp Man City sẽ diễn ra tại v&ograve;ng 16. V&igrave; vậy, kh&ocirc;ng loại trừ việc MU sẽ c&oacute; 1 v&agrave;i thay đổi thử nghiệm trong trận đấu gặp Arsenal. D&ugrave; thế n&agrave;o đi nữa,&nbsp;MU vẫn thường c&oacute; kết quả tốt tr&ecirc;n s&acirc;n Emirates v&agrave; Mourinho sẽ c&oacute; đủ chi&ecirc;u tr&ograve; để gi&uacute;p h&agrave;ng thủ số một Premier League hướng tới 1 trận đấu bất bại trước đối thủ.</p>\r\n<p style="font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 1.4; text-align: justify;"><strong>Nhiệm vụ phải thắng cho&nbsp;<a class="TextlinkBaiviet" style="font-size: 12px; text-decoration-line: none; color: #0000ff;" title="Chelsea" href="http://www.24h.com.vn/chelsea-c48e1505.html">Chelsea</a>, Man City v&agrave;&nbsp;<a class="TextlinkBaiviet" style="font-size: 12px; text-decoration-line: none; color: #0000ff;" title="Liverpool" href="http://www.24h.com.vn/liverpool-c48e1528.html">Liverpool</a>.</strong></p>\r\n<p style="font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 1.4; text-align: justify;">Ngo&agrave;i trận đại chiến Arsenal - MU, những đại gia c&ograve;n lại đều kh&ocirc;ng gặp phải đối thủ n&agrave;o kh&oacute; chịu tại v&ograve;ng 15. Liverpool sẽ l&agrave;m kh&aacute;ch tr&ecirc;n s&acirc;n đội b&oacute;ng t&acirc;n binh Brighton. Bộ đ&ocirc;i Salah - Mane đang c&oacute; phong độ cực cao, liệu đ&agrave;n "m&ograve;ng biển" c&oacute; thể cản bước "Lữ đo&agrave;n đỏ"?</p>\r\n<p style="font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 1.4; text-align: justify;">Chelsea cũng chỉ phải gặp Newcastle - đội b&oacute;ng chỉ c&oacute; 1 chiến thắng trong 9 trận gần đ&acirc;y nhất. Conte đang chỉ đạo c&aacute;c học tr&ograve; thi đấu đầy thực dụng v&agrave; trận thắng 1-0 tại v&ograve;ng 14 l&agrave; minh chứng h&ugrave;ng hồn cho sự "t&agrave;n nhẫn" của Chelsea. Newcastle c&oacute; lẽ&nbsp;kh&ocirc;ng đủ mạnh mẽ để cản bước "The Blues" v&agrave;o l&uacute;c n&agrave;y.</p>\r\n<p style="font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 1.4;" align="center"><img class="news-image" style="border: 0px; max-width: 660px;" src="http://image.24h.com.vn/upload/4-2017/images/2017-11-30/Ngoai-hang-Anh-truoc-vong-15-MU---Arsenal-nhuom-do-troi-Anh-sterling-silva-1512039660-587-width660height371.jpg" alt="Ngoại hạng Anh trước v&ograve;ng 15: Arsenal - MU dấn th&acirc;n v&agrave;o &amp;#34;tử địa&amp;#34; - 3" /></p>\r\n<p style="font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 1.4; color: #0000ff; font-style: italic; text-align: center;">Ai sẽ cản bước Man City v&agrave;o l&uacute;c n&agrave;y ?</p>\r\n<p style="font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 1.4; text-align: justify;">Dễ d&agrave;ng nhất c&oacute; lẽ l&agrave; đội đầu bảng Man City. H&agrave;ng c&ocirc;ng khủng khiếp nhất&nbsp;<em>Ngoại hạng Anh</em>&nbsp;sẽ được đ&aacute; tr&ecirc;n s&acirc;n nh&agrave; để tiếp đ&oacute;n h&agrave;ng thủ ... hớ h&ecirc;nh nhất của giải. Man City đ&atilde; ghi 44 b&agrave;n từ đầu m&ugrave;a trong khi West Ham mới để thủng lưới th&ecirc;m 4 b&agrave;n tại v&ograve;ng 14 để n&acirc;ng tổng số b&agrave;n thua của họ l&ecirc;n th&agrave;nh&nbsp;30.</p>\r\n<p style="font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 1.4; text-align: justify;">Hơn thế nữa, 2 trụ cột h&agrave;ng thủ của West Ham lại l&agrave; ... "người nh&agrave;" (Zabaleta l&agrave; cựu cầu thủ Man City. Joe Hart vẫn thuộc bi&ecirc;n chế Man City) n&ecirc;n kh&ocirc;ng kh&oacute; để c&aacute;c học tr&ograve; HLV Pep Guardiola nhanh ch&oacute;ng giải quyết trận đấu, chuẩn bị đội h&igrave;nh tốt nhất cho đại chiến th&agrave;nh Manchester tại V&ograve;ng 16.</p>\r\n<p style="font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 1.4;" align="center"><img class="news-image" style="border: 0px; max-width: 660px;" src="http://image.24h.com.vn/upload/4-2017/images/2017-11-30/Ngoai-hang-Anh-truoc-vong-15-MU---Arsenal-nhuom-do-troi-Anh-1-1512039672-632-width660height371.jpg" alt="Ngoại hạng Anh trước v&ograve;ng 15: Arsenal - MU dấn th&acirc;n v&agrave;o &amp;#34;tử địa&amp;#34; - 4" /><img class="news-image" style="border: 0px; max-width: 660px;" src="http://image.24h.com.vn/upload/4-2017/images/2017-11-30/Ngoai-hang-Anh-truoc-vong-15-MU---Arsenal-nhuom-do-troi-Anh-2-1512039672-323-width660height374.jpg" alt="Ngoại hạng Anh trước v&ograve;ng 15: Arsenal - MU dấn th&acirc;n v&agrave;o &amp;#34;tử địa&amp;#34; - 5" /></p>\r\n<p style="font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 1.4;" align="center">&nbsp;</p>\r\n<div style="font-family: Arial, Helvetica, sans-serif; font-size: 12px; text-align: center; width: 699px; clear: both; position: relative;">\r\n<div style="position: relative; max-width: 660px; margin: 0px auto;"><img class="news-image" style="border: 0px; max-width: 660px;" src="http://image.24h.com.vn/upload/4-2017/images/2017-11-30/Ngoai-hang-Anh-truoc-vong-15-MU---Arsenal-nhuom-do-troi-Anh-4-1512039672-654-width660height560.jpg" alt="Ngoại hạng Anh trước v&ograve;ng 15: Arsenal - MU dấn th&acirc;n v&agrave;o &amp;#34;tử địa&amp;#34; - 6" />\r\n<div id="24h-banner-in-image" style="position: absolute; overflow: hidden; width: 660px; height: 90px; color: white; bottom: 0px; left: 0px;">&nbsp;</div>\r\n</div>\r\n</div>\r\n<p style="font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 1.4; text-align: justify;">&nbsp;</p>\r\n<p style="font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 1.4;" align="center"><img class="news-image" style="border: 0px; max-width: 660px;" src="http://image.24h.com.vn/upload/4-2017/images/2017-11-30/Ngoai-hang-Anh-truoc-vong-15-MU---Arsenal-nhuom-do-troi-Anh-5-1512039672-16-width660height559.jpg" alt="Ngoại hạng Anh trước v&ograve;ng 15: Arsenal - MU dấn th&acirc;n v&agrave;o &amp;#34;tử địa&amp;#34; - 8" /></p>\r\n<p style="font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 1.4;" align="center"><img class="news-image" style="border: 0px; max-width: 660px;" src="http://image.24h.com.vn/upload/4-2017/images/2017-11-30/Ngoai-hang-Anh-truoc-vong-15-MU---Arsenal-nhuom-do-troi-Anh-3-1512039672-112-width660height561.jpg" alt="Ngoại hạng Anh trước v&ograve;ng 15: Arsenal - MU dấn th&acirc;n v&agrave;o &amp;#34;tử địa&amp;#34; - 9" /><img class="news-image" style="border: 0px; max-width: 660px;" src="http://image.24h.com.vn/upload/4-2017/images/2017-11-30/Ngoai-hang-Anh-truoc-vong-15-MU---Arsenal-nhuom-do-troi-Anh-6-1512039672-265-width660height561.jpg" alt="Ngoại hạng Anh trước v&ograve;ng 15: Arsenal - MU dấn th&acirc;n v&agrave;o &amp;#34;tử địa&amp;#34; - 10" /></p>', 'mins', 'Thể thao', 'uploads/df.jpg', '2017-12-01 14:58:36'),
(14, 'Hoàn thành phát triển vũ khí hạt nhân, Triều Tiên sẽ phát triển kinh tế?', 'Vào đêm 29/11, Triều Tiên đã phóng thử tên lửa đạn đạo mạnh nhất của mình. Chỉ trong vòng 6 phút sau đó, Hàn Quốc cũng tiến hành diễn tập không kích để gửi một thông điệp rằng họ sẵn sàng mạnh tay đáp trả.', '<p style="font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 1.4; text-align: justify;">Tuy nhi&ecirc;n theo h&atilde;ng tin Fox News, đ&acirc;y cũng c&oacute; thể coi l&agrave; t&iacute;n hiệu cho thấy tiến tr&igrave;nh ph&aacute;t triển vũ kh&iacute; hạt nh&acirc;n của Triều Ti&ecirc;n đang đến hồi kết.</p>\r\n<p style="font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 1.4; text-align: justify;">Một số người cho rằng, với việc tuy&ecirc;n bố l&agrave; một quốc gia hạt nh&acirc;n c&oacute; thể s&aacute;nh với Mỹ, B&igrave;nh Nhưỡng c&oacute; thể sẽ tập trung v&agrave;o một ưu ti&ecirc;n kh&aacute;c m&agrave; l&atilde;nh đạo Kim Jong-un đ&atilde; từng nếu ra trong b&agrave;i diễn văn đầu năm nay, đ&oacute; l&agrave; kh&ocirc;i phục nền kinh tế đang yếu k&eacute;m của m&igrave;nh.</p>\r\n<p style="font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 1.4;" align="center"><img class="news-image" style="border: 0px; max-width: 660px;" src="http://image.24h.com.vn/upload/4-2017/images/2017-12-01/Hoan-thanh-phat-trien-vu-khi-hat-nhan-Trieu-Tien-se-phat-trien-kinh-te-kinh-te-1512092247-123-width480height270.jpg" alt="Ho&agrave;n th&agrave;nh ph&aacute;t triển vũ kh&iacute; hạt nh&acirc;n, Triều Ti&ecirc;n sẽ ph&aacute;t triển kinh tế? - 1" /></p>\r\n<p style="font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 1.4; color: #0000ff; font-style: italic; text-align: center;">Tin Triều Ti&ecirc;n thử nghiệm t&ecirc;n lửa đ&atilde; xuất hiện tr&ecirc;n c&aacute;c k&ecirc;nh truyền th&ocirc;ng.</p>\r\n<p style="font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 1.4; text-align: justify;">Cuộc ph&oacute;ng thử t&ecirc;n lửa đạn đạo xuy&ecirc;n lục địa của Triều Ti&ecirc;n ng&agrave;y 29/11, được cho l&agrave; c&oacute; thể bắn tới bất kỳ nơi n&agrave;o thuộc l&atilde;nh thổ nước Mỹ, l&agrave; nhằm th&aacute;ch thức Mỹ cũng như chứng minh sự ph&aacute;t triển về mặt&nbsp;<a class="TextlinkBaiviet" style="text-decoration-line: none; color: #0000ff;" title="qu&acirc;n sự" href="http://www.24h.com.vn/quan-su-c705.html">qu&acirc;n sự</a>&nbsp;của m&igrave;nh. Tuy nhi&ecirc;n, B&igrave;nh Nhưỡng cũng c&oacute; những bước đi cụ thể để kh&ocirc;ng khi&ecirc;u kh&iacute;ch Tổng thống Mỹ Donald Trump d&ugrave;ng vũ lực.</p>\r\n<p style="font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 1.4; text-align: justify;">Thứ nhất, Triều Ti&ecirc;n đ&atilde; kh&ocirc;ng ph&oacute;ng t&ecirc;n lửa qua l&atilde;nh thổ&nbsp;<a class="TextlinkBaiviet" style="text-decoration-line: none; color: #0000ff;" title="Nhật Bản" href="http://www.24h.com.vn/tin-tuc-nhat-ban-c415e3809.html">Nhật Bản</a>, điều m&agrave; họ đ&atilde; hai lần thực hiện trong c&aacute;c cuộc thử nghiệm vừa qua. Thứ hai, mặc cho những tuy&ecirc;n bố mạnh mẽ, Triều Ti&ecirc;n đ&atilde; kh&ocirc;ng ph&oacute;ng t&ecirc;n lửa tới đảo Guam của Mỹ ở Th&aacute;i B&igrave;nh Dương. Thứ ba, Triều Ti&ecirc;n cũng kh&ocirc;ng cho thử nghiệm đầu đạn hạt nh&acirc;n được gắn tr&ecirc;n c&aacute;c t&ecirc;n lửa tầm xa.</p>\r\n<p style="font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 1.4; text-align: justify;">Những điều tr&ecirc;n kh&ocirc;ng phải l&agrave; những dấu hiệu r&otilde; r&agrave;ng để dự đo&aacute;n về tương lai của Triều Ti&ecirc;n. Tuy nhi&ecirc;n, n&oacute; cho thấy rằng B&igrave;nh Nhưỡng c&oacute; thể tin rằng họ đ&atilde; gi&agrave;nh chiến thắng qu&acirc;n sự lớn v&agrave; sẽ tập trung v&agrave;o c&aacute;c vấn đề kh&aacute;c trong năm tới, c&ugrave;ng thời điểm 70 năm đất nước Triều Ti&ecirc;n được th&agrave;nh lập.</p>\r\n<p style="font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 1.4; text-align: justify;">&nbsp;</p>\r\n<div class="banner_inread" style="font-family: Arial, Helvetica, sans-serif; font-size: 12px; text-align: justify;">&nbsp;</div>\r\n<p style="font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 1.4; text-align: justify;">&nbsp;</p>\r\n<p style="font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 1.4; text-align: justify;">Theo &ocirc;ng Vipin Narang, chuy&ecirc;n gia vũ kh&iacute; hạt nh&acirc;n tại MIT (Mỹ), cuộc thử nghiệm t&ecirc;n lửa mới nhất của Triều Ti&ecirc;n c&oacute; thể coi l&agrave; dấu hiệu cho thấy B&igrave;nh Nhưỡng tin đ&atilde; &ldquo;ho&agrave;n th&agrave;nh&rdquo; chương tr&igrave;nh hạt nh&acirc;n của m&igrave;nh v&agrave; tập trung v&agrave;o ph&aacute;t triển kinh tế, song &ocirc;ng cũng n&oacute;i th&ecirc;m rằng &ldquo;vẫn c&ograve;n rất nhiều yếu tố kh&oacute; lường t&aacute;c động l&ecirc;n Triều Ti&ecirc;n&rdquo;.</p>\r\n<p style="font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 1.4; text-align: justify;">&ldquo;Một phần trong t&ocirc;i vẫn lo ngại rằng Triều Ti&ecirc;n đang cố gắng để đảm bảo t&ecirc;n lửa của m&igrave;nh đạt tầm bắn như đ&atilde; thiết kế, v&agrave; rồi nếu như Mỹ vẫn nghi ngờ khả năng của t&ecirc;n lửa, họ sẽ cho thử nghiệm t&ecirc;n lửa mang đầu đạn hạt nh&acirc;n ở ngo&agrave;i biển&rdquo;, &ocirc;ng Narang n&oacute;i. &ldquo;D&ugrave; vậy t&ocirc;i vẫn nghĩ đ&acirc;y l&agrave; một động th&aacute;i đầy mạo hiểm v&agrave; chỉ được thực hiện nếu họ nhận thấy c&oacute; một h&agrave;nh động khi&ecirc;u kh&iacute;ch nghi&ecirc;m trọng từ ph&iacute;a đối thủ của m&igrave;nh&rdquo;.</p>\r\n<p style="font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 1.4; text-align: justify;">B&igrave;nh Nhưỡng ho&agrave;n to&agrave;n c&oacute; thể sẽ tiếp tục thử nghiệm vũ kh&iacute; với tốc độ ch&oacute;ng mặt như hiện nay, khi ch&uacute;ng vẫn chưa thể so s&aacute;nh được với c&aacute;c loại vũ kh&iacute; hạt nh&acirc;n m&agrave; c&aacute;c cường quốc tr&ecirc;n thế giới đang c&oacute;.</p>\r\n<p style="font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 1.4; text-align: justify;">Theo &ocirc;ng Narang, chương tr&igrave;nh vũ kh&iacute; của Triều Ti&ecirc;n vẫn đang trong giai đoạn ph&aacute;t triển, nghĩa l&agrave; &ldquo;họ vẫn cần phải đạt được những mục đ&iacute;ch nhất định về tầm bắn, độ tin cậy&rdquo;.</p>\r\n<p style="font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 1.4;" align="center"><img class="news-image" style="border: 0px; max-width: 660px;" src="http://image.24h.com.vn/upload/4-2017/images/2017-12-01/Hoan-thanh-phat-trien-vu-khi-hat-nhan-Trieu-Tien-se-phat-trien-kinh-te-kinh-te-2-1512092247-286-width480height270.jpg" alt="Ho&agrave;n th&agrave;nh ph&aacute;t triển vũ kh&iacute; hạt nh&acirc;n, Triều Ti&ecirc;n sẽ ph&aacute;t triển kinh tế? - 2" /></p>\r\n<p style="font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 1.4; color: #0000ff; font-style: italic; text-align: center;">Người d&acirc;n Triều Ti&ecirc;n vui mừng trước tin t&ecirc;n lửa đạn đạo mới được thử nghiệm th&agrave;nh c&ocirc;ng.</p>\r\n<p style="font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 1.4; text-align: justify;">Nhiều chuy&ecirc;n gia tin rằng &iacute;t nhất sẽ c&oacute; th&ecirc;m một cuộc thử nghiệm t&ecirc;n lửa nữa để kiểm chứng tầm bắn tối đa của n&oacute;. Th&ecirc;m v&agrave;o đ&oacute;, Triều Ti&ecirc;n vẫn chưa ho&agrave;n thiện t&ecirc;n lửa ph&oacute;ng từ t&agrave;u ngầm của m&igrave;nh.</p>\r\n<p style="font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 1.4; text-align: justify;">Một nỗi lo lớn hơn đ&oacute; l&agrave; Triều Ti&ecirc;n c&oacute; thể sẽ cho nổ một loại t&ecirc;n lửa mang đầu đạn nhiệt hạch ở ngo&agrave;i kh&ocirc;ng trung để dập tan nghi ngờ rằng nước n&agrave;y kh&ocirc;ng c&oacute; khả năng thu nhỏ đầu đạn hạt nh&acirc;n. V&agrave;o th&aacute;ng 9 vừa qua, Ngoại trưởng Triều Ti&ecirc;n n&oacute;i rằng đất nước của &ocirc;ng c&oacute; thể sẽ thử bom nhiệt hạch ở ngo&agrave;i Th&aacute;i B&igrave;nh Dương.</p>\r\n<p style="font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 1.4; text-align: justify;">D&ugrave; vậy, tuy&ecirc;n bố của Triều Ti&ecirc;n sau khi thử t&ecirc;n lửa v&agrave;o ng&agrave;y 29/11 cho thấy họ kh&ocirc;ng muốn g&acirc;y hoang mang. Theo họ, loại t&ecirc;n lửa n&agrave;y &ldquo;đ&atilde; đạt được mục ti&ecirc;u m&agrave; Triều Ti&ecirc;n đề ra nhằm tiến đến ho&agrave;n th&agrave;nh qu&aacute; tr&igrave;nh ph&aacute;t triển t&ecirc;n lửa&rdquo;.</p>\r\n<p style="font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 1.4; text-align: justify;">B&igrave;nh Nhưỡng cũng n&oacute;i rằng t&ecirc;n lửa của họ cho thấy họ c&oacute; thể &ldquo;ph&aacute;t triển vũ kh&iacute; để đối ph&oacute; với ch&iacute;nh s&aacute;ch hăm dọa bằng hạt nh&acirc;n của Mỹ&rdquo; trong khi người d&acirc;n tiếp tục &ldquo;c&oacute; một cuộc sống y&ecirc;n b&igrave;nh&rdquo;.</p>\r\n<p style="font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 1.4; text-align: justify;">Sau nhiều th&aacute;ng thử nghiệm t&ecirc;n lửa v&agrave; c&oacute; nhiều tuy&ecirc;n bố đe dọa lẫn nhau giữa Triều Ti&ecirc;n v&agrave; Mỹ, ai cũng muốn chương tr&igrave;nh hạt nh&acirc;n của Triều Ti&ecirc;n kết th&uacute;c. Tuy nhi&ecirc;n điều n&agrave;y c&oacute; dẫn đến việc Triều Ti&ecirc;n c&oacute; thể c&oacute; &ldquo;một cuộc sống y&ecirc;n b&igrave;nh&rdquo; hay kh&ocirc;ng, kh&ocirc;ng ai c&oacute; thể trả lời được.</p>\r\n<div class="bv-lq" style="font-family: Arial, Helvetica, sans-serif; font-size: 12px; display: inline-block; width: 699px; border-radius: 3px; background-color: #eaf8d2; border-top: 3px solid #378b36; position: relative; margin-top: 5px; text-align: justify;">\r\n<div class="bv-lq-inner">&nbsp;</div>\r\n</div>', 'mins', 'Thế giới', 'uploads/images2058039_1.jpg', '2017-12-01 15:05:26');
INSERT INTO `posts` (`id`, `title`, `description`, `content`, `author`, `category`, `image`, `created_at`) VALUES
(19, 'Ba vai phản diện hiểm hóc của Lý Liên Kiệt', 'Trong suốt sự nghiệp diễn xuất của Lý Liên Kiệt chỉ có 3 lần anh vào vai phản diện và cả ba lần đều để lại ấn tượng mạnh mẽ với người hâm mộ.', '<p style="font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 1.4; text-align: justify;"><strong>Wah Sing Ku phim&nbsp;<em style="font-size: 12px;">Vũ kh&iacute; tối thượng 4</em>&nbsp;(1998)</strong></p>\r\n<p style="font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 1.4; text-align: justify;">Năm 19 tuổi L&yacute; Li&ecirc;n Kiệt nhờ thủ vai ch&iacute;nh trong bộ phim v&otilde; thuật&nbsp;<em>Thiếu L&acirc;m Tự</em>&nbsp;đ&atilde; trở n&ecirc;n nổi tiếng khắp ch&acirc;u &Aacute;, đồng thời l&agrave;m dấy l&ecirc;n tr&agrave;o lưu v&otilde; thuật Thiếu L&acirc;m. Thập ni&ecirc;n&nbsp;1990 t&ecirc;n tuổi của L&yacute; Li&ecirc;n Kiệt tiếp tục nổi như cồn nhờ loạt phim Ho&agrave;ng Phi Hồng, qua đ&oacute; trở th&agrave;nh ng&ocirc;i sao v&otilde; thuật hạng A của điện ảnh Hoa ngữ.</p>\r\n<p style="font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 1.4; text-align: center;"><img class="news-image" style="border: 0px; max-width: 660px; width: 660px; height: 337px;" src="http://image.24h.com.vn/upload/4-2017/images/2017-11-30/1512051253-414-bat-ngo-3-vai-dien-phan-dien-hiem-hoc-hiem-co-cua-ly-lien-kiet-sao-hoa-ngu-00-1512027542-width554height283.jpg" alt="Ba vai phản diện hiểm h&oacute;c của L&yacute; Li&ecirc;n Kiệt - 1" /></p>\r\n<p style="font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 1.4; color: #0000ff; font-style: italic; text-align: center;">&Aacute;c nh&acirc;n Wah Sing Ku trong Vũ kh&iacute; tối thượng.</p>\r\n<p style="font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 1.4; text-align: justify;">Cuối thập ni&ecirc;n 1990 l&agrave; thời kỳ điện ảnh Hong Kong bắt đầu bị thu hẹp, những t&ecirc;n tuổi c&aacute;c ng&ocirc;i sao như Th&agrave;nh Long, L&yacute; Li&ecirc;n Kiệt, Ch&acirc;u Nhuận Ph&aacute;t bắt đầu t&igrave;m đến Hollywood ph&aacute;t triển sự nghiệp.</p>\r\n<p style="font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 1.4; text-align: justify;"><em>Vũ kh&iacute; tối thượng phần 4 c</em>ũng ch&iacute;nh l&agrave; t&aacute;c phẩm đầu ti&ecirc;n của L&yacute; Li&ecirc;n Kiệt khi anh đến xứ cờ hoa, s&aacute;nh vai c&ugrave;ng t&agrave;i tử Mel Gibson.</p>\r\n<div id="non-gif-image-gif-0" style="font-family: Arial, Helvetica, sans-serif; font-size: 12px;" align="center"><img id="non-gif-img-image-gif-0" class="news-image" style="border: 0px; max-width: 660px;" src="http://image.24h.com.vn/upload/4-2017/images/2017-11-30/medium/1512051253-750-bat-ngo-3-vai-dien-phan-dien-hiem-hoc-hiem-co-cua-ly-lien-kiet-ly-lien-kiet-1-1512027628-width400height167.gif" alt="Ba vai phản diện hiểm h&oacute;c của L&yacute; Li&ecirc;n Kiệt - 2" />\r\n<div align="center">&nbsp;</div>\r\n</div>\r\n<p style="font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 1.4; color: #0000ff; font-style: italic; text-align: center;">Pha h&agrave;nh động của L&yacute; Li&ecirc;n Kiệt trong phim.</p>\r\n<p style="font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 1.4; text-align: justify;">Trong phim n&agrave;y họ L&yacute; v&agrave;o vai &aacute;c nh&acirc;n Wah Sing Ku, thi triển khả năng v&otilde; nghệ cao cường khiến kh&aacute;n giả phương T&acirc;y kh&ocirc;ng khỏi kinh ngạc, nhờ vậy gi&uacute;p người h&acirc;m mộ thế giới biết đến t&ecirc;n tuổi ng&ocirc;i sao v&otilde; thuật ch&acirc;u &Aacute; với t&ecirc;n gọi Jet Li.</p>\r\n<p style="font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 1.4; text-align: center;"><img class="news-image" style="border: 0px; max-width: 660px; width: 660px; height: 277px;" src="http://image.24h.com.vn/upload/4-2017/images/2017-11-30/1512051254-700-bat-ngo-3-vai-dien-phan-dien-hiem-hoc-hiem-co-cua-ly-lien-kiet-sao-hoa-ngu-00a-1512027542-width640height269.jpg" alt="Ba vai phản diện hiểm h&oacute;c của L&yacute; Li&ecirc;n Kiệt - 4" /></p>\r\n<p style="font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 1.4; color: #0000ff; font-style: italic; text-align: center;">Đất diễn kh&ocirc;ng nhiều nhưng L&yacute; Li&ecirc;n Kiệt đủ khiến kh&aacute;n giả phương T&acirc;y kinh ngạc.</p>\r\n<p style="font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 1.4; text-align: justify;">Thực tế đất diễn của L&yacute; Li&ecirc;n Kiệt trong phim kh&ocirc;ng nhiều thế nhưng những cảnh phim ngắn ngủi khoe v&otilde; c&ocirc;ng thượng thừa của anh thực sự đ&atilde; chinh phục kh&aacute;n giả phương T&acirc;y v&agrave; khiến người xem th&ecirc;m kh&acirc;m phục. Những cảnh v&otilde; thuật của L&yacute; Li&ecirc;n Kiệt cũng đồng thời trở th&agrave;nh những cảnh quay đặc sắc của&nbsp;<em>Vũ kh&iacute; tối thượng 4.</em></p>\r\n<p style="font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 1.4; text-align: justify;">&nbsp;</p>\r\n<div class="banner_inread" style="font-family: Arial, Helvetica, sans-serif; font-size: 12px; text-align: justify;">&nbsp;</div>\r\n<p style="font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 1.4; text-align: justify;">&nbsp;</p>\r\n<p style="font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 1.4; text-align: justify;"><strong>Tần Thủy Ho&agrave;ng phim<em style="font-size: 12px;">&nbsp;X&aacute;c ướp: Lăng mộ ho&agrave;ng đế rồng</em>&nbsp;(2008)</strong></p>\r\n<p style="font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 1.4; text-align: center;">&nbsp;</p>\r\n<div style="font-family: Arial, Helvetica, sans-serif; font-size: 12px; text-align: center; width: 699px; clear: both; position: relative;">\r\n<div style="position: relative; max-width: 660px; margin: 0px auto;"><img class="news-image" style="border: 0px; max-width: 660px; width: 660px; height: 348px;" src="http://image.24h.com.vn/upload/4-2017/images/2017-11-30/1512051254-853-bat-ngo-3-vai-dien-phan-dien-hiem-hoc-hiem-co-cua-ly-lien-kiet-sao-hoa-ngu-0a-1512027542-width1200height633.jpg" alt="Ba vai phản diện hiểm h&oacute;c của L&yacute; Li&ecirc;n Kiệt - 5" />\r\n<div id="24h-banner-in-image" style="position: absolute; overflow: hidden; width: 660px; height: 90px; color: white; bottom: 0px; left: 0px;">&nbsp;</div>\r\n</div>\r\n</div>\r\n<p style="font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 1.4; text-align: justify;">&nbsp;</p>\r\n<p style="font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 1.4; text-align: justify;">Phim c&ograve;n được biết đến l&agrave; phần 3 của loạt phim&nbsp;<em>X&aacute;c ướp</em>, đồng thời l&agrave; phần phim c&oacute; sức ảnh hưởng rộng khắp tr&ecirc;n to&agrave;n thế giới.</p>\r\n<p style="font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 1.4; text-align: justify;">Bộ phim đưa c&aacute;c nh&acirc;n vật đến Trung Quốc khai quật những b&iacute; mật của triều đại đế vương cổ xưa v&agrave; v&ocirc; t&igrave;nh l&agrave;m thức tỉnh long đế Tần Thủy Ho&agrave;ng, ch&iacute;nh thức khai m&agrave;o cho trận chiến kinh ho&agrave;ng giữa thế giới cổ đại với con người hiện tại.</p>\r\n<div id="non-gif-image-gif-1" style="font-family: Arial, Helvetica, sans-serif; font-size: 12px;" align="center"><img id="non-gif-img-image-gif-1" class="news-image" style="border: 0px; max-width: 660px;" src="http://image.24h.com.vn/upload/4-2017/images/2017-11-30/medium/1512051254-982-bat-ngo-3-vai-dien-phan-dien-hiem-hoc-hiem-co-cua-ly-lien-kiet-ly-lien-kiet-2-1512027775-width435height250.gif" alt="Ba vai phản diện hiểm h&oacute;c của L&yacute; Li&ecirc;n Kiệt - 7" />\r\n<div align="center">&nbsp;</div>\r\n</div>\r\n<p style="font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 1.4; color: #0000ff; font-style: italic; text-align: center;">Cảnh h&agrave;nh động của L&yacute; Li&ecirc;n Kiệt trong vai đế long Tần Thủy Ho&agrave;ng.</p>\r\n<p style="font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 1.4; text-align: justify;">Mặc d&ugrave; danh tiếng của bộ phim kh&ocirc;ng được đ&aacute;nh gi&aacute; cao nhưng vai diễn phản diện long đế của L&yacute; Li&ecirc;n Kiệt thực sự khiến người xem kinh ngạc. Ở nh&acirc;n vật n&agrave;y to&aacute;t l&ecirc;n vẻ uy nghi, hống h&aacute;ch nhưng v&ocirc; c&ugrave;ng b&iacute; ẩn.</p>\r\n<p style="font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 1.4; text-align: justify;">Th&ecirc;m v&agrave;o đ&oacute; l&agrave; c&aacute;c hiệu ứng kỹ xảo điện ảnh c&agrave;ng khiến con người long đế họ Tần kh&eacute;t tiếng th&ecirc;m phần b&aacute; đạo. Bộ phim cũng gặt h&aacute;i doanh thu ph&ograve;ng v&eacute; to&agrave;n cầu l&ecirc;n đến 400 triệu USD.</p>\r\n<div id="non-gif-image-gif-2" style="font-family: Arial, Helvetica, sans-serif; font-size: 12px;" align="center"><img id="non-gif-img-image-gif-2" class="news-image" style="border: 0px; max-width: 660px;" src="http://image.24h.com.vn/upload/4-2017/images/2017-11-30/medium/1512051255-19-bat-ngo-3-vai-dien-phan-dien-hiem-hoc-hiem-co-cua-ly-lien-kiet-ly-lien-kiet-3-1512027805-width272height174.gif" alt="Ba vai phản diện hiểm h&oacute;c của L&yacute; Li&ecirc;n Kiệt - 9" />\r\n<div align="center">&nbsp;</div>\r\n</div>\r\n<p style="font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 1.4; color: #0000ff; font-style: italic; text-align: center;">Tần Thủy Ho&agrave;ng thức tỉnh đủ g&acirc;y khiếp sợ cho người xem.</p>\r\n<p style="font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 1.4; text-align: justify;"><strong>B&agrave;ng Thanh V&acirc;n phim&nbsp;<em style="font-size: 12px;">Đầu danh trạng</em>&nbsp;(2007)</strong></p>\r\n<p style="font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 1.4; text-align: center;"><strong><img class="news-image" style="font-size: 12px; border: 0px; max-width: 660px; width: 660px; height: 371px;" src="http://image.24h.com.vn/upload/4-2017/images/2017-11-30/1512051255-187-bat-ngo-3-vai-dien-phan-dien-hiem-hoc-hiem-co-cua-ly-lien-kiet-sao-hoa-ngu-0b-1512027542-width1280height720.jpg" alt="Ba vai phản diện hiểm h&oacute;c của L&yacute; Li&ecirc;n Kiệt - 11" /></strong></p>\r\n<p style="font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 1.4; color: #0000ff; font-style: italic; text-align: center;">Tạo h&igrave;nh ngạo nghễ v&agrave; ki&ecirc;u căng của B&agrave;ng Thanh V&acirc;n.</p>\r\n<p style="font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 1.4; text-align: justify;">Trong phim n&agrave;y L&yacute; Li&ecirc;n Kiệt vừa thủ vai nam ch&iacute;nh v&agrave; l&agrave; một vai phản diện, đ&acirc;y cũng l&agrave; vai nam ch&iacute;nh đầu ti&ecirc;n của L&yacute; Li&ecirc;n Kiệt thể hiện được sự ki&ecirc;u căng, ngạo mạn tr&ecirc;n m&agrave;n ảnh.</p>\r\n<p style="font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 1.4; text-align: justify;">Phim n&oacute;i về t&igrave;nh huynh đệ do 3 sao nam thể hiện gồm L&yacute; Li&ecirc;n Kiệt, Lưu Đức Hoa v&agrave; Kim Th&agrave;nh Vũ đ&oacute;ng. Trong đ&oacute; diễn xuất của L&yacute; Li&ecirc;n Kiệt nhận được lời ngợi khen của giới chuy&ecirc;n m&ocirc;n, khắc họa được gần như ho&agrave;n to&agrave;n t&iacute;nh c&aacute;ch hung bạo v&agrave; nghiệt ng&atilde; của nh&acirc;n vật B&agrave;ng Thanh V&acirc;n. Nhờ vậy vai diễn gi&uacute;p L&yacute; Li&ecirc;n Kiệt gi&agrave;nh giải<em>&nbsp;Nam diễn vi&ecirc;n ch&iacute;nh xuất sắc</em>&nbsp;của LHP Kim Tượng Hong Kong.</p>\r\n<div id="non-gif-image-gif-3" style="font-family: Arial, Helvetica, sans-serif; font-size: 12px;" align="center"><img id="non-gif-img-image-gif-3" class="news-image" style="border: 0px; max-width: 660px;" src="http://image.24h.com.vn/upload/4-2017/images/2017-11-30/1512051255-970-bat-ngo-3-vai-dien-phan-dien-hiem-hoc-hiem-co-cua-ly-lien-kiet-ly-lien-kiet-4-1512027974-width400height170.gif" alt="Ba vai phản diện hiểm h&oacute;c của L&yacute; Li&ecirc;n Kiệt - 12" />\r\n<div align="center">&nbsp;</div>\r\n</div>\r\n<p style="font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 1.4; color: #0000ff; font-style: italic; text-align: center;">Diễn xuất của L&yacute; Li&ecirc;n Kiệt được đ&aacute;nh gi&aacute; cao qua vai diễn phản diện độc đ&aacute;o n&agrave;y.</p>\r\n<p style="font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 1.4; text-align: justify;">Trước đ&oacute; nhiều người cho rằng L&yacute; Li&ecirc;n Kiệt vốn l&agrave; một ng&ocirc;i sao v&otilde; thuật n&ecirc;n kh&ocirc;ng mấy ch&uacute; &yacute; đến diễn xuất của anh. Thực tế nếu kh&ocirc;ng đ&oacute;ng phim h&agrave;nh động, chắc chắn L&yacute; Li&ecirc;n Kiệt cũng tỏa s&aacute;ng v&agrave; ghi dấu ấn l&agrave; một diễn vi&ecirc;n c&oacute; thực lực của điện ảnh Hoa ngữ.</p>', 'Asian Tech', 'Giải trí', 'uploads/d.jpg', '2017-12-01 18:06:15'),
(20, 'Võ sư Thiếu Lâm từ vai phụ phim Châu Tinh Trì đến đại gia bất động sản', 'Ngôi sao võ thuật được biết đến là thế hệ kế tiếp Chân Tử Đan và Lý Liên Kiệt. Anh đang sở hữu khối tài sản hàng nghìn tỷ đồng.', '<p style="font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 1.4; text-align: justify;"><strong><br class="Apple-interchange-newline" />Thế hệ thứ 4 kế thừa vai tr&ograve; ng&ocirc;i sao v&otilde; thuật điện ảnh Hoa ngữ</strong></p>\r\n<p style="font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 1.4;" align="center"><img class="news-image" style="border: 0px; max-width: 660px;" src="http://image.24h.com.vn/upload/4-2017/images/2017-11-28/1511854969-102-vo-su-thieu-lam-tu-vai-phu-phim-chau-tinh-tri-den-dai-gia-bat-dong-san-sao-hoa-ngu-00-1511167768-width669height1000.jpg" alt="V&otilde; sư Thiếu L&acirc;m từ vai phụ phim Ch&acirc;u Tinh Tr&igrave; đến đại gia bất động sản - 1" /></p>\r\n<p style="font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 1.4; color: #0000ff; font-style: italic; text-align: center;">Th&iacute;ch H&agrave;nh Vũ kh&ocirc;ng phải l&agrave; c&aacute;i t&ecirc;n qu&aacute; đ&igrave;nh đ&aacute;m nhưng lu&ocirc;n c&oacute; lượng fan v&agrave; t&aacute;c phẩm ổn định</p>\r\n<p style="font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 1.4; text-align: justify;">N&oacute;i về Th&iacute;ch H&agrave;nh Vũ, tờ&nbsp;<em>Sina</em>&nbsp;nhận định anh c&oacute; c&ocirc;ng lớn trong việc ph&aacute;t huy d&ograve;ng phim h&agrave;nh động v&otilde; thuật của điện ảnh Hoa ngữ. Bản th&acirc;n anh cũng được thừa nhận l&agrave; thế hệ kế tiếp&nbsp;c&aacute;c ng&ocirc;i sao v&otilde; thuật lừng danh như&nbsp;<a class="TextlinkBaiviet" style="text-decoration-line: none; color: #0000ff;" title="L&yacute; Tiểu Long" href="http://www.24h.com.vn/ly-tieu-long-c729e3464.html">L&yacute; Tiểu Long</a>, L&yacute; Li&ecirc;n Kiệt, Th&agrave;nh Long v&agrave; Ch&acirc;n Tử Đan.</p>\r\n<p style="font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 1.4; text-align: justify;">T&agrave;i tử sinh năm 1978 trong một gia đ&igrave;nh b&igrave;nh thường ở tỉnh Sơn Đ&ocirc;ng (Trung Quốc) với t&ecirc;n khai sinh l&agrave; Trương Thục Vũ, c&oacute; cha v&agrave; anh trai đều l&agrave;m nghề cảnh s&aacute;t.</p>\r\n<p style="font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 1.4; text-align: justify;"><strong>Bỏ nh&agrave; ra đi tầm sư học đạo v&agrave; những cơn đ&oacute;i triền mi&ecirc;n</strong></p>\r\n<p style="font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 1.4; text-align: justify;">Năm 12 tuổi, sau khi xem&nbsp;bộ phim&nbsp;<em>Thiếu L&acirc;m Tự</em>, Trương quyết t&acirc;m l&ecirc;n ch&ugrave;a theo học v&otilde; thuật cho d&ugrave; gia đ&igrave;nh phản đối kịch liệt. Một m&igrave;nh anh khăn g&oacute;i đến n&uacute;i S&ugrave;ng Sơn ở tỉnh H&agrave; Nam để b&aacute;i sư c&aacute;c v&otilde; sư ch&ugrave;a Thiếu L&acirc;m.</p>\r\n<p style="font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 1.4; text-align: justify;">Tất nhi&ecirc;n quyết định của cậu con trai ngỗ nghịch khiến cha mẹ anh v&ocirc; c&ugrave;ng tức giận v&agrave; quyết kh&ocirc;ng cho một đồng n&agrave;o, điều n&agrave;y c&oacute; nghĩa l&agrave; Trương phải tự nu&ocirc;i m&igrave;nh khi đ&atilde; dứt &aacute;o ra đi.</p>\r\n<p style="font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 1.4; text-align: justify;">Tuy vậy &ldquo;người t&iacute;nh kh&ocirc;ng bằng trời t&iacute;nh&rdquo;, Trương kh&ocirc;ng được nhận v&agrave;o ch&ugrave;a, anh đ&agrave;nh học v&otilde; tại một trường v&otilde; thuật dưới ch&acirc;n n&uacute;i trong v&ograve;ng nửa năm.</p>\r\n<p style="font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 1.4; text-align: justify;">Sau đ&oacute; Trương đến Sơn Đ&ocirc;ng v&agrave; gặp một người bạn học cũ c&oacute; anh trai tu luyện ở Thiếu L&acirc;m tự, nhờ vậy anh được xin v&agrave;o ch&ugrave;a, ch&iacute;nh thức trở th&agrave;nh đệ tử đời thứ 32 tại ng&ocirc;i danh tự về v&otilde; thuật, đồng thời quy y phật ph&aacute;p ở tuổi 12 với ph&aacute;p danh Th&iacute;ch H&agrave;nh Vũ, t&ocirc;n v&otilde; tăng đội tổng gi&aacute;o đầu ch&ugrave;a Thiếu L&acirc;m l&agrave; Đức Dương thiền sư l&agrave;m sư phụ.</p>\r\n<p style="font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 1.4; text-align: justify;">&nbsp;</p>\r\n<div class="banner_inread" style="font-family: Arial, Helvetica, sans-serif; font-size: 12px; text-align: justify;">&nbsp;</div>\r\n<p style="font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 1.4; text-align: justify;">&nbsp;</p>\r\n<p style="font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 1.4; text-align: center;"><img class="news-image" style="border: 0px; max-width: 660px; width: 660px; height: 438px;" src="http://image.24h.com.vn/upload/4-2017/images/2017-11-28/1511854969-563-vo-su-thieu-lam-tu-vai-phu-phim-chau-tinh-tri-den-dai-gia-bat-dong-san-sao-hoa-ngu-0-1511167768-width690height458.jpg" alt="V&otilde; sư Thiếu L&acirc;m từ vai phụ phim Ch&acirc;u Tinh Tr&igrave; đến đại gia bất động sản - 2" /></p>\r\n<p style="font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 1.4; color: #0000ff; font-style: italic; text-align: center;">Chịu ảnh hưởng của một bộ phim, Th&iacute;ch H&agrave;nh Vũ đ&atilde; quyết t&acirc;m l&ecirc;n ch&ugrave;a theo học v&otilde; thuật</p>\r\n<p style="font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 1.4; text-align: justify;">Trong cuộc đời, Th&iacute;ch H&agrave;nh Vũ kh&ocirc;ng sợ bất kỳ khổ cực n&agrave;o cũng như vất vả g&igrave;, anh chỉ sợ nhất l&agrave; đ&oacute;i. Nỗi sợ n&agrave;y &aacute;m ảnh anh từ năm 12 tuổi khi mới được nhận v&agrave;o Thiếu L&acirc;m tự c&ugrave;ng thu nhập mỗi th&aacute;ng 28 NDT (95 ngh&igrave;n đồng)&nbsp;từ tiền đi biểu diễn m&atilde;i v&otilde; c&ugrave;ng c&aacute;c huynh đệ v&agrave; v&otilde; sư trong ch&ugrave;a.</p>\r\n<p style="font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 1.4; text-align: justify;">Với số tiền &iacute;t ỏi n&agrave;y trong một th&aacute;ng anh phải chi tiền ăn cho ng&agrave;y 3 bữa, mỗi bữa kh&ocirc;ng được qu&aacute; 1 NDT (3,4 ngh&igrave;n đồng).</p>\r\n<p style="font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 1.4; text-align: justify;">&ldquo;<em>Hồi ấy&nbsp;mỗi ng&agrave;y t&ocirc;i đều phải l&agrave;m việc nặng b&ecirc;n cạnh luyện c&ocirc;ng. Ch&ugrave;a cũng ph&aacute;t tiền, nhưng số tiền thấp đến mức kh&ocirc;ng đủ mua cơm. Cuộc sống l&uacute;c đ&oacute; vất vả nhưng t&ocirc;i kh&ocirc;ng than nửa c&acirc;u. Đ&oacute; l&agrave; qu&aacute; tr&igrave;nh tu tập tại ch&ugrave;a Thiếu L&acirc;m, bất kỳ ai cũng phải trải qua</em>&rdquo;, Th&iacute;ch H&agrave;nh Vũ t&acirc;m sự.</p>\r\n<p style="font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 1.4; text-align: justify;">Hơn nữa một cậu nh&oacute;c 12 tuổi như Th&iacute;ch H&agrave;nh Vũ khi đ&oacute; đang ở độ tuổi ăn tuổi lớn v&agrave; phải li&ecirc;n tục vận động luyện tập v&otilde; thuật, biểu diễn n&ecirc;n nhu cầu ăn uống đầy đủ l&agrave; tất yếu. Thế nhưng cậu li&ecirc;n tục bị cơn đ&oacute;i h&agrave;nh hạ đến mức ngất xỉu. Ch&iacute;nh c&aacute;i đ&oacute;i khiến Th&iacute;ch H&agrave;nh Vũ cảm nhận được cuộc sống v&ocirc; c&ugrave;ng cơ cực nếu kh&ocirc;ng được ăn cho no, đ&acirc;y cũng trở th&agrave;nh động lực gi&uacute;p anh phấn đấu kh&ocirc;ng ngừng để sinh tồn.</p>\r\n<p style="font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 1.4; text-align: justify;">Th&iacute;ch H&agrave;nh Vũ hiểu rằng, l&uacute;c n&agrave;y chỉ c&oacute; c&aacute;c tuyệt kỹ v&otilde; c&ocirc;ng ở Thiếu L&acirc;m tự mới gi&uacute;p anh đổi đời, được ấm c&aacute;i bụng về sau. Kh&ocirc;ng những vậy anh cũng l&agrave; người đam m&ecirc; v&otilde; thuật v&agrave; li&ecirc;n tục ph&aacute;t huy cố gắng hết m&igrave;nh để lĩnh hội những g&igrave; học được từ sư phụ ở ch&ugrave;a.</p>\r\n<p style="font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 1.4; text-align: justify;">Sau 6 năm bỏ nh&agrave; quyết ch&iacute; tầm sư, đ&uacute;ng năm anh 18 tuổi cha mẹ đột ngột t&igrave;m đến ch&ugrave;a Thiếu L&acirc;m v&agrave; họ kh&ocirc;ng nhận ra cậu con trai: &ldquo;<em>Thấy t&ocirc;i vừa gầy vừa đen bố mẹ kh&ocirc;ng cầm được nước mắt, đ&oacute; cũng l&agrave; lần đầu t&ocirc;i thấy hai &ocirc;ng b&agrave; c&ugrave;ng kh&oacute;c</em>&rdquo;, H&agrave;nh Vũ nhớ lại khoảnh khắc đo&agrave;n tụ c&ugrave;ng người th&acirc;n: &ldquo;<em>Nhưng &yacute; ch&iacute; quyết t&acirc;m học v&otilde; của t&ocirc;i m&atilde;i kh&ocirc;ng bao giờ đổi, từ Thiếu L&acirc;m quyền, đao thương kiếm c&ocirc;n hay kick boxing g&igrave; t&ocirc;i cũng muốn học bằng hết v&agrave; phải cho ra hồn</em>&rdquo;.</p>\r\n<p style="font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 1.4; text-align: justify;">Nhờ đam m&ecirc; v&agrave; phấn đấu, Th&iacute;ch H&agrave;nh Vũ nhanh ch&oacute;ng được c&aacute;c tuyệt kỹ v&otilde; c&ocirc;ng trứ danh ch&ugrave;a Thiếu L&acirc;m, gi&uacute;p anh đổi đời về sau khi đi&nbsp; biểu diễn tại nước ngo&agrave;i cũng như lấn s&acirc;n điện ảnh.</p>\r\n<p style="font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 1.4; text-align: justify;"><strong>Tỏa s&aacute;ng ng&ocirc;i sao v&otilde; thuật</strong></p>\r\n<p style="font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 1.4; text-align: center;"><strong><img class="news-image" style="font-size: 12px; border: 0px; max-width: 660px; width: 660px; height: 492px;" src="http://image.24h.com.vn/upload/4-2017/images/2017-11-28/1511854969-583-vo-su-thieu-lam-tu-vai-phu-phim-chau-tinh-tri-den-dai-gia-bat-dong-san-sao-hoa-ngu-2-1511167768-width500height373.jpg" alt="V&otilde; sư Thiếu L&acirc;m từ vai phụ phim Ch&acirc;u Tinh Tr&igrave; đến đại gia bất động sản - 3" /></strong></p>\r\n<p style="font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 1.4; color: #0000ff; font-style: italic; text-align: center;">Anh từng lọt v&agrave;o mắt xanh của Ch&acirc;u Tinh Tr&igrave;</p>\r\n<p style="font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 1.4; text-align: justify;">Năm 2003 một người bạn của Th&iacute;ch H&agrave;nh Vũ l&agrave; đạo diễn ở Th&acirc;m Quyến gọi điện cho anh mời đến casting cho bộ phim&nbsp;<em>Tuyệt đỉnh Kungfu&nbsp;</em>do Ch&acirc;u Tinh Tr&igrave; d&agrave;n dựng, kết quả anh tr&uacute;ng tuyển vai diễn Khổ Lực Cường trong nh&oacute;m &ldquo;Thập nhị lộ đ&agrave;m tho&aacute;i&rdquo; v&agrave; trở th&agrave;nh một phần kh&ocirc;ng thể thiếu của bom tấn h&agrave;i nhảm để đời.</p>\r\n<p style="font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 1.4; text-align: justify;">Vai diễn n&agrave;y gi&uacute;p Th&iacute;ch H&agrave;nh Vũ nhận được sự đ&aacute;nh gi&aacute; cao của người h&acirc;m mộ lẫn c&aacute;c đồng nghiệp th&acirc;m ni&ecirc;n l&agrave; những ng&ocirc;i sao điện ảnh t&ecirc;n tuổi như Ch&acirc;n Tử Đan, Cổ Thi&ecirc;n Lạc v&agrave; Trương Gia Huy. Như vậy Th&iacute;ch H&agrave;nh Vũ đ&atilde; c&oacute; c&uacute; bứt ph&aacute; ngoạn mục khi dấn th&acirc;n showbiz, anh quyết định ho&agrave;n tục, theo đuổi sự nghiệp diễn xuất.</p>\r\n<p style="font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 1.4; text-align: center;">&nbsp;</p>\r\n<div style="font-family: Arial, Helvetica, sans-serif; font-size: 12px; text-align: center; width: 699px; clear: both; position: relative;">\r\n<div style="position: relative; max-width: 660px; margin: 0px auto;"><img class="news-image" style="border: 0px; max-width: 660px; width: 660px; height: 495px;" src="http://image.24h.com.vn/upload/4-2017/images/2017-11-28/1511854970-217-vo-su-thieu-lam-tu-vai-phu-phim-chau-tinh-tri-den-dai-gia-bat-dong-san-sao-hoa-ngu-5-1511167768-width768height576.jpg" alt="V&otilde; sư Thiếu L&acirc;m từ vai phụ phim Ch&acirc;u Tinh Tr&igrave; đến đại gia bất động sản - 4" />\r\n<div id="24h-banner-in-image" style="position: absolute; overflow: hidden; width: 660px; height: 90px; color: white; bottom: 0px; left: 0px;">&nbsp;</div>\r\n</div>\r\n</div>\r\n<p style="font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 1.4; text-align: justify;">&nbsp;</p>\r\n<p style="font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 1.4; color: #0000ff; font-style: italic; text-align: center;">Th&iacute;ch H&agrave;nh Vũ v&agrave;o vai&nbsp;Khổ Lực Cường trong "Tuyệt đỉnh Kungfu"</p>\r\n<p style="font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 1.4; text-align: justify;">B&ecirc;n cạnh đ&oacute;, anh tiếp tục ph&aacute;t huy khả năng v&otilde; nghệ của bản th&acirc;n lẫn kỹ năng diễn xuất, gi&uacute;p anh nhận được ng&agrave;y c&agrave;ng nhiều hợp đồng với c&aacute;t-x&ecirc; l&ecirc;n đến h&agrave;ng chục triệu NDT.</p>\r\n<p style="font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 1.4; text-align: justify;">Th&iacute;ch H&agrave;nh Vũ g&acirc;y được tiếng vang nhờ sự xuất hiện trong c&aacute;c bom tấn. Năm 2007 Th&iacute;ch H&agrave;nh Vũ c&ugrave;ng Ch&acirc;n Tử Đan v&agrave; Cổ Thi&ecirc;n Lạc g&oacute;p mặt trong phim&nbsp;<em>Đảo hỏa tuyến</em>, ba năm sau anh hợp t&aacute;c c&ugrave;ng d&agrave;n ng&ocirc;i sao Lưu Đức Hoa, Tạ Đ&igrave;nh Phong, Th&agrave;nh Long v&agrave; Ng&ocirc; Kinh trong bom tấn&nbsp;<em>T&acirc;n Thiếu l&acirc;m tự&nbsp;</em>với vai Tranh Kh&ocirc;ng.</p>\r\n<p style="font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 1.4; text-align: center;"><img class="news-image" style="border: 0px; max-width: 660px; width: 660px; height: 440px;" src="http://image.24h.com.vn/upload/4-2017/images/2017-11-28/1511854970-977-vo-su-thieu-lam-tu-vai-phu-phim-chau-tinh-tri-den-dai-gia-bat-dong-san-sao-hoa-ngu-6-1511167768-width640height427.jpg" alt="V&otilde; sư Thiếu L&acirc;m từ vai phụ phim Ch&acirc;u Tinh Tr&igrave; đến đại gia bất động sản - 6" /></p>\r\n<p style="font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 1.4; color: #0000ff; font-style: italic; text-align: center;">H&igrave;nh ảnh trong phim&nbsp;"Kim Cương Vương 3D"</p>\r\n<p style="font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 1.4; text-align: justify;">Năm 2013 t&agrave;i tử 39 tuổi thủ vai ch&iacute;nh trong bộ phim v&otilde; hiệp 3D&nbsp;<em>Kim Cương Vương</em>. Ngo&agrave;i ra anh c&ograve;n được t&agrave;i tử Trương Gia Huy mời v&agrave;o bộ phim điện ảnh do anh l&agrave;m đạo diễn, mang t&ecirc;n<em>&nbsp;Kẻ săn b&oacute;ng đ&ecirc;m</em>&nbsp;(2015).</p>\r\n<p style="font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 1.4; text-align: justify;">Gần đ&acirc;y, Th&iacute;ch Ngạn Năng tham gia diễn xuất trong bộ phim h&agrave;nh động Smart Chase: Fire &amp; Earth do ng&ocirc;i sao quốc tế Orlando Bloom, Nhậm Đạt Hoa, Cảnh Điềm&hellip; đ&oacute;ng ch&iacute;nh.</p>\r\n<div style="font-family: Arial, Helvetica, sans-serif; font-size: 12px;" align="center">\r\n<div class="viewVideoPlay">\r\n<div id="zplayer_cad651_container" class="media-player" style="cursor: pointer; width: 660px; height: 446px; position: relative;">\r\n<div id="zPContainer_zplayer_cad651" style="position: relative; height: 446px; background-color: rgba(200, 200, 200, 0.3);" data-setupminiplayer="1">\r\n<div id="zplayer_cad651" class="zPlayer_mainContainer jctx-host jctx-id-foo miniMode" style="overflow: hidden; top: initial; background: #000000; position: fixed; border: 0px solid; width: 280px; height: 157px; left: initial; bottom: 0px; right: 0px; visibility: visible; z-index: 9999 !important;" data-player-idx="0">\r\n<div class="zPlayer_closeMini" style="user-select: none; position: absolute; color: #ffffff; background: #c90706; z-index: 2147483647; top: 0px; right: 0px; padding: 3px; width: 15px; height: 15px; line-height: 15px; font-weight: bold; cursor: pointer;">✕</div>\r\n<div class="zPlayer_playerElement" style="user-select: none; background: none; position: absolute; top: 0px; left: 0px; bottom: 39px; right: 0px;"><video class="zPlayer_contentElement jctx-host jctx-id-foo" style="user-select: none; width: 280px; height: 118px; z-index: -9999 !important;" preload="none"></video></div>\r\n<div class="control" style="user-select: none; background: #333333; color: #cccccc; position: absolute; bottom: 0px; left: 0px; width: 280px; z-index: 99999999; height: 39px;">\r\n<div class="topControl" style="user-select: none; height: 6px; background: #1f1f1f;">\r\n<div class="progress" style="user-select: none; width: 280px; height: 6px; position: relative; float: left; cursor: pointer; background: #444444;">&nbsp;</div>\r\n<div class="pins" style="user-select: none; width: 280px; height: 30px; position: absolute; top: -26px; z-index: 9999999;">&nbsp;</div>\r\n</div>\r\n<div class="titleControl" style="font-family: arial; user-select: none; height: 30px;">\r\n<div class="titleControl_label" style="font-family: Arial, Helvetica, sans-serif; user-select: none; font-weight: bold; display: inline-block; width: 80px; line-height: 30px; color: #7ab43f;">Đang ph&aacute;t</div>\r\n&nbsp;\r\n<div class="titleControl_title" style="font-family: Arial, Helvetica, sans-serif; user-select: none; display: inline-block; width: calc(100% - 90px); vertical-align: middle; line-height: 15px;">V&otilde; sư Thiếu L&acirc;m từ vai phụ phim Ch&acirc;u Tinh Tr&igrave; đến đại gia bất động sản</div>\r\n</div>\r\n</div>\r\n</div>\r\n</div>\r\n</div>\r\n<div id="pinListzplayer_cad651">\r\n<div id="zplayer_cad651_pinInfo" class="khung-zplayer" style="border: 1px solid #dddddd; max-width: 658px; max-height: 140px; overflow-x: hidden;">\r\n<div class="khung-zplayer-container">&nbsp;</div>\r\n</div>\r\n</div>\r\n</div>\r\n</div>\r\n<p style="font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 1.4; color: #0000ff; font-style: italic; text-align: center;">Video: Những pha v&otilde; đẹp mắt của Th&iacute;ch H&agrave;nh Vũ trong c&aacute;c bộ phim anh từng g&oacute;p mặt</p>\r\n<p style="font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 1.4; text-align: justify;">Năm 2011, Th&iacute;ch H&agrave;nh Vũ lấy nghệ danh Th&iacute;ch Ngạn&nbsp;Năng. Năm 2014, anh th&agrave;nh lập<em>&nbsp;Studio Th&iacute;ch Ngạn Năng - Hoa nh&acirc;n huynh đệ</em>&nbsp;tại Th&acirc;m Quyến.</p>\r\n<p style="font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 1.4; text-align: justify;"><strong>Đại gia bất động sản</strong></p>\r\n<p style="font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 1.4; text-align: justify;">Nhờ c&aacute;t-x&ecirc; đ&oacute;ng phim c&ugrave;ng đầu &oacute;c kinh doanh nhạy b&eacute;n, Th&iacute;ch Ngạn Năng đầu tư v&agrave;o bất động sản, đặc biệt l&agrave; ở qu&ecirc; nh&agrave; tỉnh Sơn Đ&ocirc;ng.</p>\r\n<p style="font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 1.4; text-align: justify;">Nhờ tiền mua đi b&aacute;n lại v&agrave; c&oacute; con mắt tinh tường nh&igrave;n ra được những mảnh đất sinh lời của thị trường địa ốc, một năm Th&iacute;ch Ngạn Năng&nbsp;thu lợi những khoản lời lớn h&agrave;ng trăm triệu NDT.</p>\r\n<p style="font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 1.4; text-align: justify;">Cựu v&otilde; sư ch&ugrave;a Thiếu L&acirc;m đang sở hữu nhiều khối bất động sản c&oacute; gi&aacute; trị tại c&aacute;c th&agrave;nh phố lớn của Trung Quốc như Sơn Đ&ocirc;ng, Th&acirc;m Quyến, Hong Kong, Thượng Hải&hellip;</p>\r\n<p style="font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 1.4; text-align: center;"><img class="news-image" style="border: 0px; max-width: 660px; width: 660px; height: 412px;" src="http://image.24h.com.vn/upload/4-2017/images/2017-11-28/1511854970-471-vo-su-thieu-lam-tu-vai-phu-phim-chau-tinh-tri-den-dai-gia-bat-dong-san-sao-hoa-ngu-00-1511168305-width660height412.jpg" alt="V&otilde; sư Thiếu L&acirc;m từ vai phụ phim Ch&acirc;u Tinh Tr&igrave; đến đại gia bất động sản - 7" /></p>\r\n<p style="font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 1.4; color: #0000ff; font-style: italic; text-align: center;">Những cơ ngơi bất động sản tiền tỉ của "Th&iacute;ch Ngạn Năng".</p>\r\n<p style="font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 1.4; text-align: justify;">Hiện tại một m&igrave;nh ng&ocirc;i sao&nbsp;<em>Tuyệt đỉnh Kungfu</em>&nbsp;sống trong biệt phủ xa hoa trị gi&aacute; 50 triệu NDT (169 tỷ&nbsp;đồng) ở Hong Kong.</p>\r\n<p style="font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 1.4; text-align: justify;">Ngo&agrave;i ra anh c&ograve;n biếu mẹ một biệt thự ri&ecirc;ng v&agrave; tặng chị g&aacute;i một căn hộ ở qu&ecirc; nh&agrave; tỉnh Sơn Đ&ocirc;ng. Theo&nbsp;<em>Sina,</em>&nbsp;khối t&agrave;i sản của Th&iacute;ch Ngạn Năng&nbsp;hiện ước t&iacute;nh tr&ecirc;n 500 triệu NDT (1,7 ngh&igrave;n tỷ&nbsp;đồng).</p>', 'Master', 'Giải trí', 'uploads/dfdf.jpg', '2017-12-01 19:22:41'),
(21, 'Công Phượng đầu tàu U23 Việt Nam: Không mơ Quả bóng vàng', 'Trong buổi tập đầu tiên của U23 Việt Nam dưới thời HLV Parh Hang Seo, tiền đạo Công Phượng cho hay chỉ muốn tập trung 100% về chuyên môn.', '<p style="font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 1.4; text-align: justify;">S&aacute;ng&nbsp;nay (1/12),&nbsp;<strong>đội tuyển U23 Việt Nam</strong>&nbsp;đ&atilde; ch&iacute;nh thức hội qu&acirc;n v&agrave; ngay buổi chiều HLV Park Hang Seo đ&atilde; y&ecirc;u cầu thực hiện buổi tập luyện đầu ti&ecirc;n. C&aacute;c cầu thủ c&oacute; mặt tr&ecirc;n s&acirc;n l&uacute;c 15h30, tập luyện đến khoảng hơn 17h. Theo ghi nhận của ph&oacute;ng vi&ecirc;n, gi&aacute;o &aacute;n d&agrave;nh cho&nbsp;<a class="TextlinkBaiviet" style="text-decoration-line: none; color: #0000ff;" title="U23 Việt Nam" href="http://www.24h.com.vn/u23-viet-nam-c48e3423.html">U23 Việt Nam</a>&nbsp;kh&ocirc;ng hề nhẹ nh&agrave;ng.</p>\r\n<p style="font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 1.4;" align="center"><img class="news-image" style="border: 0px; max-width: 660px;" src="http://image.24h.com.vn/upload/4-2017/images/2017-12-01/Cong-Phuong-dau-tau-U23-Viet-Nam-Khong-mo-Qua-bong-vang-cp1-1512129686-357-width660height474.jpg" alt="C&ocirc;ng Phượng đầu t&agrave;u U23 Việt Nam: Kh&ocirc;ng mơ Quả b&oacute;ng v&agrave;ng - 1" /></p>\r\n<p style="font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 1.4; color: #0000ff; font-style: italic; text-align: center;" align="center">C&ocirc;ng Phượng đi b&oacute;ng trong buổi tập</p>\r\n<p style="font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 1.4; text-align: justify;">Sau qu&atilde;ng thời gian đầu khởi động v&agrave; ban huấn luyện phổ biến gi&aacute;o &aacute;n, cường độ tập luyện được tăng dần. HLV Park Hang Seo x&acirc;y dựng &yacute; đồ rất r&otilde; r&agrave;ng v&agrave; y&ecirc;u cầu c&aacute;c cầu thủ tập trung mảng miếng tấn c&ocirc;ng bi&ecirc;n. Cụ thể, c&aacute;c cầu thủ chia th&agrave;nh 2 đội v&agrave; tập c&aacute;c pha tạt c&aacute;nh rất đặc trưng của chiến lược gia người H&agrave;n Quốc.</p>\r\n<p style="font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 1.4; text-align: justify;">Kết th&uacute;c buổi tập, d&ugrave; c&aacute;c cầu thủ kh&aacute; mệt mỏi song C&ocirc;ng Phượng vẫn d&agrave;nh thời gian trả lời b&aacute;o giới. Theo y&ecirc;u cầu của ban huấn luyện, truyền th&ocirc;ng kh&ocirc;ng được ph&eacute;p t&aacute;c nghiệp khi đội tuyển tập chuy&ecirc;n m&ocirc;n n&ecirc;n cuộc trao đổi ngắn với C&ocirc;ng Phượng c&agrave;ng trở n&ecirc;n qu&yacute; gi&aacute;.</p>\r\n<p style="font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 1.4; text-align: justify;">Vai tr&ograve; của&nbsp;<em>C&ocirc;ng Phượng</em>&nbsp;tại đội tuyển U23 Việt Nam rất kh&aacute;c biệt. Tiền đạo xứ Nghệ l&agrave; một trong những cầu thủ d&agrave;y dạn kinh nghiệm v&agrave; bản lĩnh thi đấu nhất. N&oacute;i c&aacute;ch kh&aacute;c, C&ocirc;ng Phượng xứng đ&aacute;ng l&agrave; đầu t&agrave;u cho to&agrave;n đội. Tất nhi&ecirc;n, ch&acirc;n s&uacute;t đang kho&aacute;c &aacute;o HAGL nhận thức rất r&otilde; tr&aacute;ch nhiệm n&agrave;y.</p>\r\n<p style="font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 1.4; text-align: justify;">"T&ocirc;i ở đội U23 đ&atilde; l&acirc;u. T&ocirc;i l&agrave; người đi trước, c&oacute; kinh nghiệm n&ecirc;n sẽ hướng dẫn động vi&ecirc;n c&aacute;c anh em c&ugrave;ng nhau phấn đấu để tiến bộ về mọi mặt", C&ocirc;ng Phượng chia sẻ.</p>\r\n<p style="font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 1.4; text-align: justify;">C&ocirc;ng Phượng cũng chia sẻ về giải đấu giao hữu tới đ&acirc;y ở Th&aacute;i Lan: "Đ&acirc;y l&agrave; giải giao hữu v&agrave; ch&uacute;ng t&ocirc;i đang cố gắng chuẩn bị tốt nhất trước khi l&ecirc;n đường. U23 Việt Nam kh&ocirc;ng đặt nặng vấn đề th&agrave;nh t&iacute;ch, mục đ&iacute;ch quan trọng nhất l&agrave; cọ x&aacute;t".</p>\r\n<p style="font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 1.4; text-align: justify;">Khi được hỏi về cuộc đua gi&agrave;nh Quả b&oacute;ng V&agrave;ng Việt Nam 2017, C&ocirc;ng Phượng kh&ocirc;ng thực sự c&oacute; &yacute; muốn tranh gi&agrave;nh. Tiền đạo xứ Nghệ tin rằng ban tổ chức sẽ t&igrave;m ra được người xứng đ&aacute;ng nhất để t&ocirc;n vinh. "Đ&acirc;y l&agrave; vấn đề chuy&ecirc;n m&ocirc;n v&agrave; t&ocirc;i kh&ocirc;ng c&oacute; &yacute; kiến g&igrave; nhiều. Nếu được bầu chọn, t&ocirc;i sẽ chọn Đinh Thanh Trung",&nbsp;<u>C&ocirc;ng Phượng</u>&nbsp;chia sẻ.</p>', 'Star', 'Thể thao', 'uploads/gfg.jpg', '2017-12-01 19:27:57'),
(22, 'Chàng trai bán pin mặt trời trở thành triệu phú USD nhờ cách thức bất ngờ', 'Bằng cách lập website bán đồ Trung Quốc nhập từ Alibaba và Aliexpress, Trevor Chapman đã kiếm được 1 triệu USD trong vòng 3 tháng.', '<p style="font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 1.4; text-align: justify;">C&aacute;ch đ&acirc;y 13 năm, Chapman l&agrave; một sinh vi&ecirc;n ngh&egrave;o kh&oacute;, phải vay tiền đ&oacute;ng học ph&iacute; v&agrave; muối mặt g&otilde; cửa từng nh&agrave; rao b&aacute;n thuốc diệt c&ocirc;n tr&ugrave;ng để kiếm tiền sinh hoạt ph&iacute;. Nhiều năm sau, khi đ&atilde; t&iacute;ch trữ đủ vốn, anh bắt đầu kinh doanh ri&ecirc;ng, chuy&ecirc;n lắp đặt, ph&acirc;n phối pin mặt trời. Việc kinh doanh tiến triển thuận lợi, nhanh ch&oacute;ng mở rộng quy m&ocirc; ra 3 bang l&acirc;n cận. Tuy nhi&ecirc;n, Trevor vẫn li&ecirc;n tục băn khoăn về định hướng trong tương lai.</p>\r\n<p style="font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 1.4;" align="center"><img class="news-image" style="border: 0px; max-width: 660px;" src="http://image.24h.com.vn/upload/4-2017/images/2017-12-01/1512099340-308-chang-trai-ban-pin-mat-troi-tro-thanh-trieu-phu-usd-nho-cach-thuc-bat-ngo-trevor-chapman-1-1512038379-width600height400.jpg" alt="Ch&agrave;ng trai b&aacute;n pin mặt trời trở th&agrave;nh triệu ph&uacute; USD nhờ c&aacute;ch thức bất ngờ - 1" /></p>\r\n<p style="font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 1.4; color: #0000ff; font-style: italic; text-align: center;">Doanh nh&acirc;n Trevor Chapman</p>\r\n<p style="font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 1.4; text-align: justify;">Chapman b&agrave;y tỏ: &ldquo;T&ocirc;i tự hỏi ch&iacute;nh m&igrave;nh, khi đ&atilde; gần 30 tuổi, t&ocirc;i c&oacute; sẵn s&agrave;ng chờ đến khi con c&aacute;i trưởng th&agrave;nh rồi mới tận hưởng cuộc sống như m&igrave;nh mong muốn?&rdquo;. V&agrave; rồi, c&acirc;u n&oacute;i của Warren Buffett vang vọng trong đầu anh: &ldquo;Nếu kh&ocirc;ng t&igrave;m được c&aacute;ch kiếm tiền trong l&uacute;c đang ngủ, anh sẽ phải l&agrave;m việc cho đến khi l&igrave;a xa c&otilde;i đời n&agrave;y&rdquo;.</p>\r\n<p style="font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 1.4; text-align: justify;">Cũng ch&iacute;nh từ đ&acirc;y, anh quyết t&acirc;m đầu tư v&agrave;o thương mại điện tử, x&acirc;y dựng trang web LDSman.com, chuy&ecirc;n kinh doanh c&aacute;c mặt h&agrave;ng c&oacute; xuất xứ từ Trung Quốc. Chapman đ&atilde; bắt đầu một c&aacute;ch v&ocirc; c&ugrave;ng thận trọng. Anh vẫn tiếp tục kinh doanh pin mặt trời, v&agrave; d&agrave;nh thời gian nghi&ecirc;n cứu dự &aacute;n n&agrave;y sau giờ l&agrave;m việc.</p>\r\n<p style="font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 1.4; text-align: justify;">Ban đầu, Chapman mua 1 t&ecirc;n miền trị gi&aacute; 2,99 USD (tương đương 68 ngh&igrave;n VNĐ)/năm, lập t&agrave;i khoản b&aacute;n h&agrave;ng tr&ecirc;n Shopifu với gi&aacute; d&ugrave;ng thử 14 USD (tương đương 318 ngh&igrave;n VNĐ). Anh chi 100 USD (tương đương 2,3 triệu VNĐ) mỗi ng&agrave;y cho ng&acirc;n s&aacute;ch quảng c&aacute;o v&agrave; ch&iacute;nh thức triển khai dự &aacute;n từ ng&agrave;y 11/11/2016.</p>\r\n<p style="font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 1.4; text-align: justify;">Ng&agrave;y đầu ti&ecirc;n, Chapman đ&atilde; bị lỗ vốn do lựa chọn b&aacute;n c&aacute;c t&aacute;c phẩm nghệ thuật, mặt h&agrave;ng k&eacute;n người mua. Sau đ&oacute;, anh chuyển sang b&aacute;n ghế bơm hơi &ndash; sản phẩm đang rất được ưa chuộng. Chapman t&igrave;m nguồn h&agrave;ng từ c&aacute;c h&atilde;ng sản xuất Trung Quốc tr&ecirc;n Alibaba v&agrave; Aliexpress, mua gi&aacute; gốc khoảng 4,99 USD (tương đương 113 ngh&igrave;n VNĐ) v&agrave; b&aacute;n lại với gi&aacute; 59,99 USD (tương đương 1,4 triệu VNĐ).</p>\r\n<p style="font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 1.4; text-align: justify;">&nbsp;</p>\r\n<div class="banner_inread" style="font-family: Arial, Helvetica, sans-serif; font-size: 12px; text-align: justify;">&nbsp;</div>\r\n<p style="font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 1.4; text-align: justify;">&nbsp;</p>\r\n<p style="font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 1.4;" align="center"><img class="news-image" style="border: 0px; max-width: 660px;" src="http://image.24h.com.vn/upload/4-2017/images/2017-12-01/1512099340-102-chang-trai-ban-pin-mat-troi-tro-thanh-trieu-phu-usd-nho-cach-thuc-bat-ngo-trevor-chapman-2-1512038379-width600height400.jpg" alt="Ch&agrave;ng trai b&aacute;n pin mặt trời trở th&agrave;nh triệu ph&uacute; USD nhờ c&aacute;ch thức bất ngờ - 2" /></p>\r\n<p style="font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 1.4; color: #0000ff; font-style: italic; text-align: center;">Kho h&agrave;ng của Chapman</p>\r\n<p style="font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 1.4; text-align: justify;">Để tr&aacute;nh chi ph&iacute; rủi ro của việc t&iacute;ch trữ h&agrave;ng, anh thỏa thuận với c&aacute;c nh&agrave; cung cấp Trung Quốc để họ chuyển trực tiếp từ kho của m&igrave;nh đến cho kh&aacute;ch h&agrave;ng. Chapman cho rằng đ&acirc;y l&agrave; c&aacute;ch hiệu quả để đo mức độ được ưa chuộng của mỗi sản phẩm. B&ecirc;n cạnh đ&oacute;, nhờ một thỏa thuận c&oacute; t&ecirc;n ePacket giữa nền bưu ch&iacute;nh tại Mỹ với c&aacute;c nước kh&aacute;c, nhằm khuyến kh&iacute;ch thương mại điện tử, ph&iacute; chuyển h&agrave;ng từ Trung Quốc tới Mỹ giảm đ&aacute;ng kể, gi&uacute;p Chapman giảm bớt rủi ro trong qu&aacute; tr&igrave;nh kinh doanh.</p>\r\n<p style="font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 1.4; text-align: justify;">Chỉ sau 2 tuần, anh kiếm được 10.000 USD đầu ti&ecirc;n. Website của anh c&ograve;n b&aacute;n nhiều sản phẩm độc đ&aacute;o từ Trung Quốc như kem đ&aacute;nh răng c&oacute; th&agrave;nh phần than hoạt t&iacute;nh hay đồ chơi độc đ&aacute;o tại nước bạn.</p>\r\n<p style="font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 1.4; text-align: justify;">Từ số tiền n&agrave;y, Chapman thu&ecirc; nh&acirc;n vi&ecirc;n chăm s&oacute;c kh&aacute;ch h&agrave;ng tại Philippines với 700 USD/người/th&aacute;ng. Anh cũng gia tăng ng&acirc;n s&aacute;ch quảng c&aacute;o tr&ecirc;n Facebook để thu h&uacute;t nhiều kh&aacute;ch h&agrave;ng hơn.</p>\r\n<p style="font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 1.4; text-align: justify;">Đồng thời, anh mua một nh&agrave; kho rộng hơn 800m2 v&agrave; tuyển 5 nh&acirc;n vi&ecirc;n l&agrave;m việc to&agrave;n thời gian. Việc n&agrave;y cho ph&eacute;p LDSman đưa việc kinh doanh l&ecirc;n một tầm cao mới, gi&uacute;p Chapman giảm giờ l&agrave;m xuống c&ograve;n 1 tiếng mỗi tuần v&agrave; ch&iacute;nh thức ngừng kinh doanh pin năng lượng mặt trời.</p>\r\n<p style="font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 1.4;" align="center"><img class="news-image" style="border: 0px; max-width: 660px;" src="http://image.24h.com.vn/upload/4-2017/images/2017-12-01/1512099340-803-chang-trai-ban-pin-mat-troi-tro-thanh-trieu-phu-usd-nho-cach-thuc-bat-ngo-trevor-chapman-3-1512038379-width600height320.jpg" alt="Ch&agrave;ng trai b&aacute;n pin mặt trời trở th&agrave;nh triệu ph&uacute; USD nhờ c&aacute;ch thức bất ngờ - 3" /></p>\r\n<p style="font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 1.4; color: #0000ff; font-style: italic; text-align: center;">Khi thời gian l&agrave;m việc được r&uacute;t ngắn, Chapman c&oacute; thể tận hưởng những kỳ nghỉ b&ecirc;n gia đ&igrave;nh</p>\r\n<p style="font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 1.4; text-align: justify;">Hiện doanh thu hằng th&aacute;ng của LDSman v&agrave;o khoảng 350.000 USD một th&aacute;ng. Chapman cho biết anh vừa chạm mốc doanh thu 2 triệu USD trong th&aacute;ng thứ 6 hoạt động. Anh cũng tham gia giảng dạy một kho&aacute; thương mại điện tử tại Mỹ.</p>\r\n<p style="font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 1.4; text-align: justify;">Gần đ&acirc;y, anh phối hợp với anh rể, mở một c&ocirc;ng ty chuyển ph&aacute;t, tận dụng khoảng trống tr&ecirc;n c&aacute;c chuyến bay chở h&agrave;ng h&oacute;a để đưa h&agrave;ng từ ch&acirc;u &Aacute; sang Mỹ. Kh&aacute;ch h&agrave;ng mới nhất của họ ch&iacute;nh l&agrave; Amazon. Họ kỳ vọng c&oacute; thể thu về 10 triệu USD trong năm đầu hoạt động. V&agrave; dĩ nhi&ecirc;n, phần lớn nh&acirc;n vi&ecirc;n cũng l&agrave; thu&ecirc; ngo&agrave;i.</p>\r\n<p style="font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 1.4; text-align: justify;">Qu&aacute; tr&igrave;nh kinh doanh đ&atilde; gi&uacute;p Chapman nhận ra thương mại điện tử ch&iacute;nh l&agrave; cơ hội tốt nhất cho bất kỳ ai muốn kiểm so&aacute;t thời gian v&agrave; kiếm tiền. Kể cả khi đ&oacute; chỉ l&agrave; người kh&ocirc;ng c&oacute; kinh nghiệm như anh.</p>', 'Junior', 'Kinh Tế', 'uploads/sss.jpg', '2017-12-01 19:30:22');
INSERT INTO `posts` (`id`, `title`, `description`, `content`, `author`, `category`, `image`, `created_at`) VALUES
(23, '5 loại thực phẩm ngăn ngừa mất thính lực', 'Magiê, kẽm, omega-3, vitamin D có trong thực phẩm có thể giúp bạn ngăn ngừa mất thính lực.', '<p style="font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 1.4; text-align: justify;"><strong>Một số c&aacute;ch ngăn ngừa mất th&iacute;nh lực</strong></p>\r\n<p style="font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 1.4; text-align: justify;">C&oacute; nhiều c&aacute;ch để duy tr&igrave; th&iacute;nh gi&aacute;c khỏe mạnh. Bảo vệ đ&ocirc;i tai của bạn bằng hạn chế tiếp x&uacute;c với tiếng ồn độc hại v&agrave; giữ kh&ocirc;ng nhiễm tr&ugrave;ng tai l&agrave; vấn đề tất nhi&ecirc;n. Tuy nhi&ecirc;n, một lối sống l&agrave;nh mạnh với si&ecirc;ng tập thể dục, một chế độ ăn uống c&acirc;n bằng v&agrave; đủ vitamin c&oacute; thể gi&uacute;p ngăn chặn hoặc tr&igrave; ho&atilde;n&nbsp;mất th&iacute;nh lực.</p>\r\n<p style="font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 1.4; text-align: justify;"><strong>5 loại thực phẩm c&oacute; thể ngăn ngừa mất th&iacute;nh lực&nbsp;Chất b&eacute;o Omega 3 &amp; Vitamin D như l&agrave; c&aacute;c chất bổ cho th&iacute;nh lực</strong></p>\r\n<p style="font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 1.4; text-align: justify;">Chất b&eacute;o Omega 3&nbsp;v&agrave; Vitamin D thường c&oacute; trong c&aacute; như c&aacute; hồi, c&aacute; ngừ, c&aacute; m&ograve;i c&oacute; thể c&oacute; những t&aacute;c động rất t&iacute;ch cực đến&nbsp;th&iacute;nh lực. C&aacute;c nghi&ecirc;n cứu đ&atilde; chỉ ra rằng người lớn ăn c&aacute; hai lần một tuần c&oacute; một cơ hội thấp hơn 42% phải đối mặt với mất th&iacute;nh lực li&ecirc;n quan đến tuổi t&aacute;c so với những người kh&ocirc;ng ăn c&aacute;. L&yacute; do ch&iacute;nh, nghi&ecirc;n cứu đ&atilde; kết luận, đ&oacute; l&agrave; chất b&eacute;o Omega 3 tăng cường c&aacute;c mạch m&aacute;u trong hệ thống cảm nhận &acirc;m thanh của tai. C&oacute; thể n&oacute;i &ldquo;C&aacute; l&agrave; bạn của tai&rdquo;.</p>\r\n<p style="font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 1.4;" align="center"><img class="news-image" style="border: 0px; max-width: 660px;" src="http://image.24h.com.vn/upload/4-2017/images/2017-11-30/5-loai-thuc-pham-ngan-ngua-mat-thinh-luc-1-1512006676-644-width600height352.jpg" alt="5 loại thực phẩm ngăn ngừa mất th&iacute;nh lực - 1" /></p>\r\n<p style="font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 1.4; color: #0000ff; font-style: italic; text-align: center;">Thực phẩm ngăn ngừa mất th&iacute;nh lực</p>\r\n<p style="font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 1.4; text-align: justify;"><strong>Chất chống oxy h&oacute;a v&agrave; acid folic như&nbsp;chất&nbsp;bổ sung cho th&iacute;nh lực</strong></p>\r\n<p style="font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 1.4; text-align: justify;">Một lượng thường xuy&ecirc;n của c&aacute;c chất chống oxy h&oacute;a, đặc biệt c&oacute; th&agrave;nh phần của acid folic thường được t&igrave;m thấy trong rau cải b&oacute; x&ocirc;i, măng t&acirc;y, đậu, b&ocirc;ng cải xanh, trứng, gan, c&aacute;c loại hạt c&oacute; thể l&agrave;m&nbsp;giảm nguy cơ mất th&iacute;nh lực&nbsp;l&ecirc;n đến 20%. Chất chống oxy h&oacute;a hoạt động như chất bổ sung mất th&iacute;nh lực bằng c&aacute;ch giảm số lượng c&aacute;c gốc tự do tr&agrave;n ngập trong cơ thể của bạn, c&aacute;c gốc tự do c&oacute; thể g&acirc;y tổn hại c&aacute;c m&ocirc; thần kinh ở tai trong của bạn. Nhớ lu&ocirc;n ăn rau để bảo vệ th&iacute;nh lực của bạn!</p>\r\n<p style="font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 1.4; text-align: justify;">&nbsp;</p>\r\n<div class="banner_inread" style="font-family: Arial, Helvetica, sans-serif; font-size: 12px; text-align: justify;">&nbsp;</div>\r\n<p style="font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 1.4; text-align: justify;">&nbsp;</p>\r\n<p style="font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 1.4; text-align: justify;"><strong>Magi&ecirc; l&agrave; một chất bổ sung th&iacute;nh lực</strong></p>\r\n<p style="font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 1.4; text-align: justify;">Magnesium, thường được t&igrave;m thấy trong chuối, khoai t&acirc;y, atis&ocirc; hoặc b&ocirc;ng cải xanh, đ&atilde; được chứng minh để cung cấp bảo vệ bổ sung chống lại tiếng ồn g&acirc;y ra mất th&iacute;nh lực. Ăn 5 tr&aacute;i chuối một ng&agrave;y c&oacute; gi&aacute; trị trong bảo vệ&nbsp;th&iacute;nh lực.</p>\r\n<p style="font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 1.4; text-align: justify;"><strong>Kẽm l&agrave; một&nbsp;&ldquo;Vitamin&rdquo;&nbsp;cho th&iacute;nh lực</strong></p>\r\n<p style="font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 1.4; text-align: justify;">Bạn c&oacute; thể tăng sức đề kh&aacute;ng tai trong của bạn đối với&nbsp;mất th&iacute;nh lực&nbsp;li&ecirc;n quan đến tuổi bằng c&aacute;ch giữ một liều l&agrave;nh mạnh của kẽm. Kẽm c&oacute; thể được t&igrave;m thấy trong chocolate đen hoặc h&agrave;u.</p>\r\n<p style="font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 1.4; text-align: justify;"><strong>Vitamin C, E &amp; Glutathione l&agrave; chất bổ sung cho th&iacute;nh lực</strong></p>\r\n<p style="font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 1.4; text-align: justify;">Tương tự như c&aacute;c chất chống oxy h&oacute;a, Vitamin C / E hoạt động như chất bổ sung&nbsp;th&iacute;nh lực&nbsp;bằng c&aacute;ch kiểm so&aacute;t c&aacute;c gốc tự do v&agrave; tăng cường hệ thống miễn dịch to&agrave;n th&acirc;n của bạn, do đ&oacute; l&agrave;m giảm nguy cơ nhiễm tr&ugrave;ng tai. C&aacute;c nguồn Vitamin C / E dễ d&agrave;ng để t&igrave;m thấy trong rau quả (v&iacute; dụ cam, ớt&hellip;).</p>\r\n<p style="font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 1.4; text-align: justify;">C&aacute;c th&agrave;nh phần tr&ecirc;n đ&acirc;y l&agrave; những yếu tố ch&iacute;nh của bất kỳ chế độ ăn uống l&agrave;nh mạnh c&oacute; thể l&agrave;m tăng sức khỏe của bạn. Nếu bạn muốn l&agrave;m thế n&agrave;o để ngăn ngừa mất th&iacute;nh lực chưa cần đến gi&uacute;p đỡ của m&aacute;y trợ th&iacute;nh,&nbsp;thực phẩm&nbsp;chứa c&aacute;c chất dinh dưỡng v&agrave; bổ sung cho th&iacute;nh lực l&agrave; một giải ph&aacute;p dễ t&igrave;m kiếm, hợp tự nhi&ecirc;n, ph&ugrave; hợp với sinh l&yacute; cơ thể v&agrave; ho&agrave;n to&agrave;n c&oacute; thể ngăn ngừa mất th&iacute;nh lực v&agrave; bảo tồn th&iacute;nh lực cho bạn trong hiện tại v&agrave; tương lai.</p>', 'Senior', 'Sức khỏe', 'uploads/dfđ.jpg', '2017-12-01 20:08:44'),
(24, 'Xót xa, nhiều bệnh nhân chết tức tưởi chỉ vì tin…thầy bói', 'Chia sẻ với PV, nhiều bác sĩ giật mình cảnh báo đã có hàng loạt ca bệnh chết tức tưởi chỉ vì tin lời thầy bói.', '<p style="font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 1.4; text-align: justify;">Chia sẻ với PV, Ths.BS Nguyễn Trung Cấp, Trưởng Khoa Cấp cứu, Bệnh viện Nhiệt đới Trung ương nhớ lại trường hợp chị M. 33 tuổi ở Th&aacute;i B&igrave;nh bị vi&ecirc;m m&ocirc; mềm c&aacute;nh tay v&agrave; vai, vi&ecirc;m m&agrave;ng n&atilde;o, loạn thần n&oacute;i nhảm. Trong l&uacute;c chị M. đang điều trị ổn định th&igrave; gia đ&igrave;nh nằng nặc xin về v&igrave; thầy b&oacute;i bảo ở bệnh viện sẽ kh&ocirc;ng khỏi, cứ nghe thầy, đưa về nh&agrave; c&uacute;ng sẽ khỏi. Sau 5 ng&agrave;y, chị M. lại quay v&agrave;o viện trong t&igrave;nh trạng v&ugrave;ng vi&ecirc;m ở c&aacute;nh tay lan hết l&ecirc;n ngực, nhiễm tr&ugrave;ng m&aacute;u, sốc kh&ocirc;ng hồi phục v&agrave; sau đ&oacute; tử vong.</p>\r\n<p style="font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 1.4;" align="center"><img class="news-image" style="border: 0px; max-width: 660px;" src="http://image.24h.com.vn/upload/4-2017/images/2017-11-30/1512010567-536-rung-dong-nhieu-benh-nhan-chet-tuc-tuoi-chi-vi-tinthay-boi-1-1512010183-width640height480.jpg" alt="X&oacute;t xa, nhiều bệnh nh&acirc;n chết tức tưởi chỉ v&igrave; tin&hellip;thầy b&oacute;i - 1" /></p>\r\n<p style="font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 1.4; color: #0000ff; font-style: italic; text-align: center;">BS Cấp kể lại những trường hợp m&ecirc; t&iacute;n dị đoan, chết tức tưởi</p>\r\n<p style="font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 1.4; text-align: justify;">Một trường hợp kh&aacute;c, 42 tuổi ở Tuy&ecirc;n Quang nghe m&aacute;ch bảo uống thuốc l&aacute; để "bổ gan" n&ecirc;n bị suy gan cấp. Đang điều trị, gia đ&igrave;nh bệnh nh&acirc;n xin về v&agrave; n&oacute;i với b&aacute;c sĩ: &ldquo;Cảm ơn c&aacute;c b&aacute;c đễ hết l&ograve;ng hết sức cứu chữa. Nhưng gia đ&igrave;nh đ&atilde; đi xem b&oacute;i, số phận của em t&ocirc;i c&oacute; đến đ&acirc;y th&ocirc;i".</p>\r\n<p style="font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 1.4; text-align: justify;">BS Cấp nhớ lại một trường hợp bệnh nh&acirc;n ở H&agrave; Nam nhập viện c&aacute;ch đ&acirc;y 3 năm đang điều trị vi&ecirc;m n&atilde;o nhưng nghe lời thầy b&oacute;i, gia đ&igrave;nh nhất quyết đ&ograve;i k&yacute; bệnh &aacute;n xin về nh&agrave; để c&uacute;ng. C&uacute;ng xong v&agrave;i tiếng, bệnh nh&acirc;n n&agrave;y lại v&agrave;o viện. May mắn, bệnh nh&acirc;n được chữa khỏi.</p>\r\n<p style="font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 1.4; text-align: justify;">Tại Bệnh viện Việt Đức, b&aacute;c sĩ Lưu Quang Th&ugrave;y -&nbsp;Trung t&acirc;m G&acirc;y m&ecirc; v&agrave; Hồi sức Bệnh viện Việt Đức (H&agrave; Nội) cho biết, c&aacute;ch đ&acirc;y kh&ocirc;ng l&acirc;u, một nam bệnh nh&acirc;n đ&atilde; tử vong sau khi người vợ nhất quyết bắt chồng xuất viện đưa về nh&agrave; v&igrave; nghe lời thầy b&oacute;i ph&aacute;n "kiểu g&igrave; cũng chết".</p>\r\n<p style="font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 1.4; text-align: justify;">&nbsp;</p>\r\n<div class="banner_inread" style="font-family: Arial, Helvetica, sans-serif; font-size: 12px; text-align: justify;">&nbsp;</div>\r\n<p style="font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 1.4; text-align: justify;">&nbsp;</p>\r\n<p style="font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 1.4; text-align: justify;">Đ&aacute;ng n&oacute;i l&agrave;, ngay l&uacute;c sức khỏe bệnh nh&acirc;n tiến triển tốt, người vợ đến gặp b&aacute;c sĩ v&agrave; xin cho chồng về nh&agrave;. D&ugrave; b&aacute;c sĩ hết lời can ngăn v&agrave; cho biết t&igrave;nh h&igrave;nh bệnh nh&acirc;n đang kh&aacute; l&ecirc;n từng ng&agrave;y, mong gia đ&igrave;nh cố gắng ki&ecirc;n tr&igrave; hơn. Tuy nhi&ecirc;n, gia đ&igrave;nh vẫn quyết đưa bệnh nh&acirc;n về, kh&ocirc;ng tiếp tục điều trị nữa.</p>\r\n<p style="font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 1.4; text-align: justify;">"Chị vợ bảo đ&atilde; đi xem 5 thầy b&oacute;i, ai cũng n&oacute;i bệnh nh&acirc;n chắc chắn kh&ocirc;ng qua khỏi n&ecirc;n muốn đưa chồng về nh&agrave; d&ugrave; ch&uacute;ng t&ocirc;i đ&atilde; hết sức can ngăn. Sức khỏe bệnh nh&acirc;n cũng đang kh&aacute; l&ecirc;n, ch&uacute;ng t&ocirc;i cũng rất tiếc nhưng kh&ocirc;ng thể l&agrave;m g&igrave; kh&aacute;c", b&aacute;c sĩ Th&ugrave;y cho biết.</p>\r\n<p style="font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 1.4;" align="center"><img class="news-image" style="border: 0px; max-width: 660px;" src="http://image.24h.com.vn/upload/4-2017/images/2017-11-30/1512010567-836-rung-dong-nhieu-benh-nhan-chet-tuc-tuoi-chi-vi-tinthay-boi-2-1512010183-width640height480.jpg" alt="X&oacute;t xa, nhiều bệnh nh&acirc;n chết tức tưởi chỉ v&igrave; tin&hellip;thầy b&oacute;i - 2" /></p>\r\n<p style="font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 1.4; color: #0000ff; font-style: italic; text-align: center;">Nhiều người nguy kịch, tử vong v&igrave; tin lời thầy b&oacute;i</p>\r\n<p style="font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 1.4; text-align: justify;">Trưởng Khoa Cấp cứu Bệnh viện Nhiệt đới Trung ương cho biết, những trường hợp tr&ecirc;n l&agrave; nạn nh&acirc;n của nạn m&ecirc; t&iacute;n dị đoan. Khi nghe tin bệnh nh&acirc;n qua đời, c&aacute;c b&aacute;c sĩ v&ocirc; c&ugrave;ng buồn l&ograve;ng v&igrave; trước đ&oacute; rất t&iacute;ch cực cứu chữa cho bệnh nh&acirc;n, chỉ v&igrave; một lời của thầy b&oacute;i khiến việc điều trị bị ngừng lại dẫn đến hậu quả đ&aacute;ng tiếc.</p>\r\n<p style="font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 1.4; text-align: justify;">&ldquo;Thầy thuốc kh&ocirc;ng can thiệp v&agrave;o t&iacute;n ngưỡng, t&acirc;m linh của người bệnh v&agrave; gia đ&igrave;nh. Việc c&uacute;ng b&aacute;i, đi xem b&oacute;i l&agrave; quyền của mỗi c&aacute; nh&acirc;n, mỗi gia đ&igrave;nh. Nhưng c&aacute;c thầy c&uacute;ng, thầy b&oacute;i can thiệp v&agrave;o qu&aacute; tr&igrave;nh điều trị, hoặc khuy&ecirc;n bảo bệnh nh&acirc;n v&agrave; gia đ&igrave;nh bỏ điều trị l&agrave; v&ocirc; đạo đức, tước mất quyền được chữa bệnh của bệnh nh&acirc;n&rdquo;, Ths.BS Nguyễn Trung Cấp bức x&uacute;c.</p>\r\n<p style="font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 1.4; text-align: justify;">Tin lời thầy b&oacute;i, bỏ điều trị ảnh hưởng nghi&ecirc;m trọng đến sức khỏe v&agrave; t&iacute;nh mạng của con người. BS Cấp khuyến c&aacute;o, mọi người n&ecirc;n tin tưởng v&agrave;o khoa học, tr&aacute;nh những c&aacute;i chết tức tưởi chỉ v&igrave; mấy lời ph&aacute;n của thầy b&oacute;i.</p>\r\n<div class="bv-lq" style="font-family: Arial, Helvetica, sans-serif; font-size: 12px; display: inline-block; width: 699px; border-radius: 3px; background-color: #eaf8d2; border-top: 3px solid #378b36; position: relative; margin-top: 5px; text-align: justify;">\r\n<div class="bv-lq-inner">&nbsp;</div>\r\n</div>', 'Senior', 'Sức khỏe', 'uploads/vvvv.jpg', '2017-12-01 20:11:15'),
(25, '100 tỉ phú giàu nhất thế giới năm 2017 theo học những ngành gì?', 'Theo kết quả nghiên cứu được đăng tải trên tạp chí The Independent, 100 tỉ phú giàu nhất thế giới tập trung lựa chọn theo học 5 chuyên ngành sau.', '<p style="font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 1.4; text-align: justify;">Theo nghi&ecirc;n cứu của tổ chức chuy&ecirc;n về lao động v&agrave; việc l&agrave;m Aaron Wallis Sales Recruitment, Anh cho biết, &nbsp;trong danh s&aacute;ch 100 người gi&agrave;u nhất thế giới năm 2017 do tạp ch&iacute; Forbes b&igrave;nh chọn c&oacute; tới 75 người đ&atilde; từng tốt nghiệp đại học. Phần lớn trong số họ lựa chọn theo học chuy&ecirc;n ng&agrave;nh kỹ thuật.</p>\r\n<p style="font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 1.4; text-align: justify;">Kết quả cho thấy rằng, số tỉ ph&uacute; bắt đầu sự nghiệp với vị tr&iacute; kinh doanh chứng kho&aacute;n v&agrave; nh&acirc;n vi&ecirc;n b&aacute;n h&agrave;ng chiếm số lượng lớn nhất.</p>\r\n<p style="font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 1.4; text-align: justify;">Chỉ c&oacute; 17% tỉ ph&uacute; trong danh s&aacute;ch 100 người gi&agrave;u nhất thế giới bắt đầu sự nghiệp bằng việc tự đứng l&ecirc;n kinh doanh. Phần lớn trong số họ sau khi tốt nghiệp đều bắt đầu sự nghiệp với vị tr&iacute; l&agrave; nh&acirc;n vi&ecirc;n cho c&aacute;c c&ocirc;ng ty, tổ chức.</p>\r\n<p style="font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 1.4;" align="center"><img class="news-image" style="border: 0px; max-width: 660px;" src="http://image.24h.com.vn/upload/4-2017/images/2017-12-01/1512119887-849-top-100-ti-phu-giau-nhat-the-gioi-nam-2017-hoc-nhung-chuyen-nganh-gi-1-1512119110-width660height365.jpg" alt="100 tỉ ph&uacute; gi&agrave;u nhất thế giới năm 2017 theo học những ng&agrave;nh g&igrave;? - 1" /></p>\r\n<p style="font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 1.4; text-align: justify;"><strong>5 ng&agrave;nh nghề được c&aacute;c tỉ ph&uacute; h&agrave;ng đầu thế giới lựa chọn theo học:</strong></p>\r\n<p style="font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 1.4; text-align: justify;">- Kỹ thuật: 22%</p>\r\n<p style="font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 1.4; text-align: justify;">- Kinh doanh: 16%</p>\r\n<p style="font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 1.4; text-align: justify;">- T&agrave;i ch&iacute;nh, kinh tế: 11%</p>\r\n<p style="font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 1.4; text-align: justify;">- Luật: 6%</p>\r\n<p style="font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 1.4; text-align: justify;">- Khoa học m&aacute;y t&iacute;nh: 4%</p>\r\n<p style="font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 1.4; text-align: justify;"><strong>5 c&ocirc;ng việc phổ biến của c&aacute;c tỉ ph&uacute; sau khi vừa tốt nghiệp đại học:</strong></p>\r\n<p style="font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 1.4; text-align: justify;">- B&aacute;n h&agrave;ng: 10%</p>\r\n<p style="font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 1.4; text-align: justify;">- Kinh doanh chứng kho&aacute;n: 9%</p>\r\n<p style="font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 1.4; text-align: justify;">- Nh&acirc;n vi&ecirc;n ph&aacute;t triển phần mềm: 5%</p>\r\n<p style="font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 1.4; text-align: justify;">- Kỹ sư: 5%</p>\r\n<p style="font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 1.4; text-align: justify;">- Chuy&ecirc;n vi&ecirc;n ph&acirc;n t&iacute;ch: 4%</p>\r\n<p style="font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 1.4; text-align: justify;">Theo gi&aacute;m đốc Rob Scott của Aaron Wallis Sales Recruitment cho biết: &ldquo;Ch&uacute;ng ta thấy rằng phần lớn c&aacute;c tỉ ph&uacute; gi&agrave;u nhất thế giới đều sở hữu tấm bằng đại học v&agrave; bắt đầu c&ocirc;ng việc kinh doanh sau khi tốt nghiệp. Điều đ&oacute; cho thấy rằng, tốt nghiệp đại học l&agrave; bước đầu quan trọng để chuẩn bị cho sự nghiệp của bạn trong tương lai&rdquo;.</p>', 'Tiểu Phàm', 'Giáo dục', 'uploads/ddd.jpg', '2017-12-01 20:13:21'),
(26, 'Man City – Pep độc bá nhưng MU - Mourinho có "ADN chiến thắng"', 'Man City dường như đang không thể ngăn cản nhưng hãy nhớ MU vẫn ở trên đường đua vô địch Ngoại hạng Anh mùa này, theo nhận định của cựu hậu vệ Danny Mills với tờ Dailymail.', '<p style="font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 1.4; text-align: justify;">ể từ sau trận h&ograve;a Everton hồi đầu m&ugrave;a,&nbsp;<strong>Man City</strong>&nbsp;đ&atilde; to&agrave;n thắng khắp đấu trường, c&agrave;n qu&eacute;t mọi đối thủ lớn nhỏ. Ri&ecirc;ng ở Ngoại hạng Anh, &ldquo;The Citizens&rdquo; bỏ c&aacute;ch k&igrave;nh địch MU tới 8 điểm. Rất nhiều chuy&ecirc;n gia v&agrave; người h&acirc;m mộ đều dự đo&aacute;n, với sức mạnh chiều s&acirc;u đội h&igrave;nh c&ugrave;ng lối chơi ăn khu&ocirc;n, lợi thế lớn điểm số, Man xanh sẽ chẳng kh&oacute; để xưng vương trong nước.</p>\r\n<p style="font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 1.4; text-align: justify;"><img class="news-image" style="border: 0px; max-width: 660px;" src="http://image.24h.com.vn/upload/4-2017/images/2017-12-01/Man-City--Pep-doc-ba-nhung-MU---Mourinho-co-mu2-1512097252-841-width660height371.jpg" alt="Man City &ndash; Pep độc b&aacute; nhưng MU - Mourinho c&oacute; &amp;#34;ADN chiến thắng&amp;#34; - 1" /></p>\r\n<p style="font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 1.4; color: #0000ff; font-style: italic; text-align: center;">MU quyết bắt kịp&nbsp;<a class="TextlinkBaiviet" style="text-decoration-line: none; color: #0000ff;" title="Man City" href="http://www.24h.com.vn/manchester-city-c48e4403.html">Man City</a>&nbsp;giai đoạn n&agrave;y</p>\r\n<p style="font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 1.4; text-align: justify;">Tuy nhi&ecirc;n cựu hậu vệ Danny Mills kh&ocirc;ng nghĩ vậy, bởi anh đ&aacute;nh gi&aacute; cao MU cũng như ma m&atilde;nh của&nbsp;<a class="TextlinkBaiviet" style="text-decoration-line: none; color: #0000ff;" title="Jose Mourinho" href="http://www.24h.com.vn/mourinho-ve-mu-c48e3841.html">Jose Mourinho</a>&nbsp;&ndash; người c&oacute; kinh nghiệm ăn đứt Pep Guardiola ở cuộc đua đường trường khốc liệt Ngoại hạng Anh. Điều quan trọng, v&agrave;o ng&agrave;y 10/12 tới, &ldquo;Quỷ đỏ&rdquo; sẽ đấu Man City tr&ecirc;n s&acirc;n nh&agrave; Old Trafford, nơi họ đang to&agrave;n thắng tới 10 trận m&ugrave;a n&agrave;y.</p>\r\n<p style="font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 1.4; text-align: justify;">&ldquo;Man City dường như đang kh&ocirc;ng thể chặn đứng. Họ chơi tuyệt vời dưới b&agrave;n tay của Pep. Nhưng h&atilde;y nhớ ở nửa đỏ th&agrave;nh phố Manchester c&ograve;n c&oacute; Mourinho lĩnh ấn. &Ocirc;ng ấy l&agrave; người thực dụng, giỏi b&agrave;y binh ph&ograve;ng thủ. &Ocirc;ng ấy đ&atilde; chứng tỏ m&igrave;nh giỏi thế n&agrave;o. &Ocirc;ng ấy đơn giản l&agrave; người chiến thắng. T&ocirc;i tin rằng trận derby Manchester sẽ rất hấp dẫn&rdquo;, Danny Mills ph&acirc;n t&iacute;ch.</p>\r\n<p style="font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 1.4; text-align: justify;">&ldquo;Từ đầu m&ugrave;a, t&ocirc;i đ&atilde; chấm MU tr&ecirc;n cửa Man City trong cuộc đua v&ocirc; địch Ngoại hạng Anh m&ugrave;a n&agrave;y v&igrave; t&ocirc;i kh&ocirc;ng chắc chắn về sự phản ứng của Man City ra sao sau một m&ugrave;a giải thất bại to&agrave;n tập. C&aacute;c bản hợp đồng mới thế n&agrave;o, c&oacute; th&iacute;ch nghi ngay kh&ocirc;ng? Liệu triết l&yacute; của Pep Guardiola v&agrave; c&aacute;ch vận h&agrave;nh của &ocirc;ng ấy c&oacute; mang lại hiệu quả?&rdquo;.</p>\r\n<p style="font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 1.4; text-align: justify;">&nbsp;</p>\r\n<div style="font-family: Arial, Helvetica, sans-serif; font-size: 12px; text-align: center; width: 699px; clear: both; position: relative;">\r\n<div style="position: relative; max-width: 660px; margin: 0px auto;"><img class="news-image" style="border: 0px; max-width: 660px;" src="http://image.24h.com.vn/upload/4-2017/images/2017-12-01/Man-City--Pep-doc-ba-nhung-MU---Mourinho-co-mou-1512097252-988-width660height486.jpg" alt="Man City &ndash; Pep độc b&aacute; nhưng MU - Mourinho c&oacute; &amp;#34;ADN chiến thắng&amp;#34; - 2" />\r\n<div id="24h-banner-in-image" style="position: absolute; overflow: hidden; width: 660px; height: 90px; color: white; bottom: 0px; left: 0px;">&nbsp;</div>\r\n</div>\r\n</div>\r\n<p style="font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 1.4; text-align: justify;">&nbsp;</p>\r\n<p style="font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 1.4; color: #0000ff; font-style: italic; text-align: center;">Mourinho c&oacute; kinh nghiệm vượt trội Pep ở Ngoại hạng Anh</p>\r\n<p style="font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 1.4; text-align: justify;">D&ugrave; l&agrave; người cũ của Man City (2004-2009) nhưng cựu hậu vệ Danny Mills rất ngưỡng mộ Mourinho bởi anh được tận mắt chứng kiến thời đại huy ho&agrave;ng của &ldquo;Người đặc biệt&rdquo; ở Ngoại hạng Anh với Chelsea 1 thập kỷ trước. &ldquo;Mourinho sinh ra để chiến thắng. &Ocirc;ng ấy đ&atilde; chinh phục giải đấu nhiều lần. Giống như MU vậy. Đ&oacute; l&agrave; th&aacute;ch thức lớn cho Pep&rdquo;, Mills n&oacute;i th&ecirc;m.</p>\r\n<p style="font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 1.4; text-align: justify;">Ở v&ograve;ng 15 cuối tuần n&agrave;y, MU sẽ phải l&agrave;m kh&aacute;ch&nbsp;<a class="TextlinkBaiviet" style="text-decoration-line: none; color: #0000ff;" title="Arsenal" href="http://www.24h.com.vn/arsenal-fc-c48e3424.html">Arsenal</a>&nbsp;trong khi Man City tiếp West Ham. Nếu khoảng c&aacute;ch kh&ocirc;ng thay đổi, trận derby Manchester ở v&ograve;ng kế tiếp sẽ s&ocirc;i sục hơn.</p>', 'Dương Quá', 'Thể thao', 'uploads/ps.jpg', '2017-12-01 20:16:34'),
(27, 'Động đất mạnh gần thành phố hơn 800.000 dân của Iran', 'Một trận động đất mạnh 6 độ xảy ra ở đông nam Iran, gần thành phố Kerman với dân số hơn 800.000 người.', '<table class="tplCaption" style="margin: 10px auto; padding: 0px; box-sizing: border-box; text-rendering: geometricPrecision; max-width: 100%; color: #333333; font-family: arial; width: 500px;" border="0" cellspacing="0" cellpadding="3" align="center">\r\n<tbody style="margin: 0px; padding: 0px; box-sizing: border-box; text-rendering: geometricPrecision;">\r\n<tr style="margin: 0px; padding: 0px; box-sizing: border-box; text-rendering: geometricPrecision;">\r\n<td style="margin: 0px; padding: 0px; box-sizing: border-box; text-rendering: geometricPrecision; line-height: 0;"><img class="lazyInitial lazyLoaded" style="margin: 0px; padding: 0px; box-sizing: border-box; text-rendering: geometricPrecision; border: 0px; font-size: 0px; line-height: 0; max-width: 100%;" src="https://i-vnexpress.vnecdn.net/2017/12/01/11580-kerman-locator-map-9435-1512100403.jpg" alt="Vị tr&iacute; th&agrave;nh phố Kerman, Iran. Đồ họa: World Atlas." data-natural-width="500" data-was-processed="true" data-width="500" data-pwidth="500" /></td>\r\n</tr>\r\n<tr style="margin: 0px; padding: 0px; box-sizing: border-box; text-rendering: geometricPrecision;">\r\n<td style="margin: 0px; padding: 0px; box-sizing: border-box; text-rendering: geometricPrecision; line-height: 0;">\r\n<p class="Image" style="margin: 0px; padding: 10px; box-sizing: border-box; text-rendering: geometricPrecision; font-stretch: normal; font-size: 12px; line-height: normal; font-family: arial; background: #f5f5f5;">Vị tr&iacute; th&agrave;nh phố Kerman, Iran. Đồ họa:&nbsp;<em style="margin: 0px; padding: 0px; box-sizing: border-box; text-rendering: geometricPrecision;">World Atlas.</em></p>\r\n</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class="Normal" style="margin: 0px 0px 1em; padding: 0px; box-sizing: border-box; text-rendering: geometricPrecision; font-stretch: normal; line-height: 18px; font-family: arial; color: #333333;">Trận động đất mạnh 6 độ xảy ra v&agrave;o 6h32 s&aacute;ng nay gần th&agrave;nh phố Kerman, đ&ocirc;ng nam Iran. T&acirc;m chấn ở độ s&acirc;u khoảng 10 km, c&aacute;ch Kerman 58 km,&nbsp;<em style="margin: 0px; padding: 0px; box-sizing: border-box; text-rendering: geometricPrecision;">AFP</em>dẫn th&ocirc;ng tin từ Cơ quan Khảo s&aacute;t Địa chất Mỹ (USGS) cho biết. Kerman c&oacute; d&acirc;n số hơn 821.000 người.</p>\r\n<p class="Normal" style="margin: 0px 0px 1em; padding: 0px; box-sizing: border-box; text-rendering: geometricPrecision; font-stretch: normal; line-height: 18px; font-family: arial; color: #333333;">Theo USGS, hai dư chấn mạnh 5 v&agrave; 5,1 độ xuất hiện sau đ&oacute;.</p>\r\n<p class="Normal" style="margin: 0px 0px 1em; padding: 0px; box-sizing: border-box; text-rendering: geometricPrecision; font-stretch: normal; line-height: 18px; font-family: arial; color: #333333;">"Hiện chưa c&oacute; th&ocirc;ng tin về thương vong. Một số ng&ocirc;i l&agrave;ng c&oacute; thiệt hại", Hossein-Ali Mehrabizadeh, quan chức cơ quan ứng ph&oacute; khủng hoảng Kerman, n&oacute;i.</p>\r\n<p class="Normal" style="margin: 0px 0px 1em; padding: 0px; box-sizing: border-box; text-rendering: geometricPrecision; font-stretch: normal; line-height: 18px; font-family: arial; color: #333333;">Trung t&acirc;m ứng ph&oacute; khủng khoảng Kerman được k&iacute;ch hoạt, c&aacute;c nh&oacute;m cứu hộ thuộc Chữ thập Đỏ v&agrave; ch&iacute;nh quyền địa phương nhanh ch&oacute;ng tới những khu vực chịu ảnh hưởng để đ&aacute;nh gi&aacute; sơ bộ t&igrave;nh h&igrave;nh.</p>\r\n<p class="Normal" style="margin: 0px 0px 1em; padding: 0px; box-sizing: border-box; text-rendering: geometricPrecision; font-stretch: normal; line-height: 18px; font-family: arial; color: #333333;">Động đất 6 độ được coi l&agrave; mạnh v&agrave; c&oacute; thể g&acirc;y thiệt hại nghi&ecirc;m trọng. M&ocirc; h&igrave;nh của USGS dự b&aacute;o trận động đất &iacute;t khả năng g&acirc;y thiệt hại về người hay t&agrave;i sản tr&ecirc;n diện rộng.</p>\r\n<p class="Normal" style="margin: 0px 0px 1em; padding: 0px; box-sizing: border-box; text-rendering: geometricPrecision; font-stretch: normal; line-height: 18px; font-family: arial; color: #333333;">Iran hai tuần trước hứng chịu trận động đất mạnh 7,3 độ, l&agrave;m hơn 400 người thiệt mạng ở tỉnh ph&iacute;a t&acirc;y Kermanshah, s&aacute;t bi&ecirc;n giới với Iraq.</p>\r\n<p class="Normal" style="margin: 0px 0px 1em; padding: 0px; box-sizing: border-box; text-rendering: geometricPrecision; font-stretch: normal; line-height: 18px; font-family: arial; color: #333333;">Iran nằm tr&ecirc;n hai mảng kiến tạo lớn, thường xuy&ecirc;n c&oacute; hoạt động địa chất. Năm 1990, một trận động đất mạnh 7,4 độ xảy ra ở miền bắc Iran l&agrave;m 40.000 người chết, 300.000 người bị thương, nửa triệu người mất nh&agrave; cửa. 13 năm sau, một trận động đất mạnh san phẳng nhiều khu vực ở th&agrave;nh phố Bam, đ&ocirc;ng nam Iran, l&agrave;m &iacute;t nhất 31.000 người chết.</p>', 'Kiều Phong', 'Thế giới', 'uploads/dfnb.jpg', '2017-12-01 21:21:06');

